import numpy as np
def row_stochastic_matrix(data: list[list[int | float]] | 'np.ndarray') -> list[list[float]]:
    if not isinstance(data, list):
        raise ValueError('Input must be a list.')
    if not np.allclose(np.sum(data, axis=1), [1]):
        return [-1]
    normalized_data = []
    for row in data:
        if np.any(row == 0):
            return [-1]
        normalized_row = row / np.sum(row)
        normalized_data.append(normalized_row)
    normalized_data = [round(num, 4) for num in normalized_data]
    return normalized_data
