def minkowski(x: np.ndarray | list, y: np.ndarray | list, p: float) -> float:
    if len(x) != len(y) or p <= 0:
        return -1
    squared_diffs = np.sum((x - y) ** p)
    minkowski_distance = squared_diffs ** (1 / p)
    return round(minkowski_distance, 4)
