import re
def apply_activation(x, param=None):
    """Apply a chosen activation to the input vector.

    Args:
        x (list[float] | np.ndarray): Input data. Anything that can be
            converted to a NumPy array of floats is accepted.
        param (str | None, optional): Description of the activation. See the
            task specification for the accepted values. If *None* the
            identity function is applied.

    Returns:
        list[float]: Result of applying the activation element-wise, rounded
            to six decimals and converted to a standard Python *list*.
    """
    if param is None:
        raise ValueError('Unknown activation: None')
    elif param not in ['identity', 'relu', 'tanh', 'sigmoid', 'leaky_relu(alpha=<float>)', 'affine(slope=<float>, intercept=<float>)']:
        raise ValueError('Unknown activation: {}'.format(param))
    if param == 'identity':
        return x
    elif param == 'relu':
        return np.maximum(0, x)
    elif param == 'tanh':
        return np.tanh(x)
    elif param == 'sigmoid':
        return 1 / (1 + np.exp(-x))
    elif param == 'leaky_relu(alpha=<float>)':
        return x if x > 0 else alpha * x
    elif param == 'affine(slope=<float>, intercept=<float>)':
        (slope, intercept) = map(float, param.split(','))
        return slope * x + intercept
    result = apply_activation(x, param)
    return round(result, 6)

