def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """
    if name == 'accuracy':

        def accuracy(y_true: list, y_pred: list) -> float:
            correct = sum((y_true[i] == y_pred[i] for i in range(len(y_true))))
            return correct / len(y_true)
    elif name == 'precision':

        def precision(y_true: list, y_pred: list) -> float:
            if not y_pred:
                return 0
            tp = sum((y_true[i] == 1 and y_pred[i] == 1 for i in range(len(y_true))))
            fp = sum((y_true[i] == 0 and y_pred[i] == 1 for i in range(len(y_true))))
            return tp / (tp + fp) if tp + fp else 0
    elif name == 'recall':

        def recall(y_true: list, y_pred: list) -> float:
            if not y_pred:
                return 0
            tp = sum((y_true[i] == 1 and y_pred[i] == 1 for i in range(len(y_true))))
            fn = sum((y_true[i] == 0 and y_pred[i] == 1 for i in range(len(y_true))))
            return tp / (tp + fn) if tp + fn else 0
    elif name == 'f1':

        def f1(y_true: list, y_pred: list) -> float:
            if not y_pred:
                return 0
            tp = sum((y_true[i] == 1 and y_pred[i] == 1 for i in range(len(y_true))))
            fn = sum((y_true[i] == 0 and y_pred[i] == 1 for i in range(len(y_true))))
            precision = tp / (tp + fp) if tp + fp else 0
            recall = tp / (tp + fn) if tp + fn else 0
            return 2 * precision * recall / (precision + recall)
    else:
        return -1

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    predictions = np.mean(y_train, axis=0)
    for _ in range(n_estimators):
        residuals = y_train - predictions
        tree = np.ctree(residuals, max_depth=max_depth, criterion='mse')
        predictions += tree.predict(X_test) * learning_rate
    predictions_rounded = np.round(predictions, decimals=4)
    return predictions_rounded.tolist()
