def calculate_covariance_matrix(X: np.ndarray | list[list[int | float]]) -> list[list[float]]:
    if isinstance(X, list):
        X = np.array(X)
    if len(X) < 2:
        return np.zeros((len(X), len(X)))
    mu_x = np.mean(X, axis=0)
    mu_y = np.mean(X, axis=1)
    cov_matrix = np.sum((X - mu_x) * (X - mu_y) / (len(X) - 1), axis=0)
    return round(cov_matrix, 4)

