def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    n_samples = data.shape[0]
    labels = [1] * n_samples
    neighbors = {i: [] for i in range(n_samples)}
    labels[0] = 1
    neighbors[0].append(0)
    for i in range(1, n_samples):
        if i == 0:
            continue
        if labels[i] == 1:
            for neighbor in neighbors[i]:
                if labels[neighbor] == 0:
                    neighbors[neighbor].append(i)
        for neighbor in neighbors[i]:
            if labels[neighbor] != 0:
                continue
        core_neighbors = [j for j in neighbors[i] if labels[j] == 1]
        labels[i] = len(labels) + 1
        for neighbor in core_neighbors:
            neighbors[neighbor].remove(i)
    return labels
