import numpy as np
def hann(window_len: int, symmetric: bool=False) -> list[float]:
    if window_len <= 0:
        return []
    if window_len == 1:
        return [1.0]
    if symmetric:
        h = np.cos(np.pi * np.arange(window_len) / (window_len - 1))
        h[window_len // 2] = 0
        h = h[:-1]
    else:
        h = np.cos(np.pi * np.arange(window_len) / (window_len - 1))
        h = h[:-1]
    h_rounded = [round(val, 4) for val in h]
    return h_rounded

