from collections import defaultdict
def topological_sort(vertices: list, edges: list[tuple]) -> List[int] | None:
    adj_list = defaultdict(list)
    for (u, v) in edges:
        adj_list[u].append(v)

    def dfs(vertex):
        visited[vertex] = True
        for neighbor in adj_list[vertex]:
            if not visited[neighbor]:
                dfs(neighbor)
        result.append(vertex)
    visited = {vertex: False for vertex in vertices}
    for vertex in vertices:
        if not visited[vertex]:
            dfs(vertex)
    if any((not visited[vertex] for vertex in vertices)):
        return None
    result.reverse()
    return result

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if isinstance(x, (int, float)):
        if order == 0:
            return 1 / (1 + np.exp(-x))
        elif order == 1:
            return (1 - sigmoid(x)) * x
        elif order == 2:
            return (1 - sigmoid(x)) * (1 - 2 * sigmoid(x))
        else:
            return -1
    elif isinstance(x, list):
        result = []
        for item in x:
            result.append(sigmoid(item, order))
        return result
    elif isinstance(x, np.ndarray):
        result = np.array([sigmoid(item, order) for item in x])
        return result.tolist()
    else:
        raise TypeError('Input must be a scalar, list, or NumPy array.')

import numpy as np
def to_categorical(y: list[int] | np.ndarray, num_classes: int | None=None) -> list[list[int]]:
    if num_classes is None:
        max_class_index = np.max(y)
    else:
        max_class_index = num_classes
    if max_class_index < len(y):
        return [-1]
    one_hot_matrix = np.zeros((len(y), max_class_index + 1))
    for (i, label) in enumerate(y):
        one_hot_matrix[i, label] = 1
    return one_hot_matrix.tolist()

