def aggregate_predictions(predictions: list[list[list[float]]]) -> tuple[list[list[float]], list[int]]:

    def weighted_average(probabilities):
        total_weight = np.sum(probabilities, axis=0)
        weighted_sum = np.average(probabilities, weights=total_weight, axis=0)
        return round(weighted_sum, 4)

    def find_max_probability(probabilities):
        max_prob = np.max(probabilities, axis=0)
        return np.argmax(max_prob)
    averaged_probabilities = [weighted_average(estimator) for estimator in predictions]
    rounded_probabilities = [round(p, 4) for p in averaged_probabilities]
    max_index = find_max_probability(rounded_probabilities)
    return (rounded_probabilities, max_index)
