def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:

    def _build_tree(X, y, depth=0):
        if len(X) == 0:
            return None
        mse = np.mean((y - np.mean(y)) ** 2)
        current_leaf = None
        for feature_index in range(X.shape[1]):
            sorted_indices = np.argsort(X[:, feature_index])
            left = X[sorted_indices[:depth]]
            right = X[sorted_indices[depth + 1:]]
            left_tree = _build_tree(left, y[left], depth + 1)
            right_tree = _build_tree(right, y[right], depth + 1)
            if left_tree is not None and right_tree is not None:
                current_leaf = np.mean(y[left_tree])
        return current_leaf
    root = _build_tree(X_train, y_train, 0)
    if root is None:
        return []
    predictions = [root]
    for point in X_test:
        next_node = root
        while next_node is not None:
            if next_node.left is not None:
                next_node = next_node.left
            else:
                next_node = next_node.right
            predictions.append(next_node)
    return predictions
