def tree_stats(root: 'node | None') -> tuple[int, int]:
    if root is None:
        return (0, 0)
    leaf_count = sum((1 for node in traverse(root) if node.results is not None))
    max_depth = max_depth_recursive(root)
    return (leaf_count, max_depth)
def traverse(node: 'node') -> list['node']:
    """Traverse the tree to find all leaf nodes."""
    if node is None:
        return []
    else:
        return [node] + traverse(node.left) + traverse(node.right)
def max_depth_recursive(node: 'node') -> int:
    """Compute the maximum depth of the tree rooted at the given node."""
    if node is None:
        return 0
    else:
        return max(max_depth_recursive(node.left), max_depth_recursive(node.right)) + 1

