def forward_step(A: list[list[float]], B: list[list[float]], S: list[float], obs: list[int], t: int) -> list[float]:
    if not isinstance(A, list) or not isinstance(B, list) or (not isinstance(S, list)) or (not isinstance(obs, list)):
        return -1
    (n, m) = (len(A), len(B))
    if t < 0 or t >= n:
        return -1
    if n != len(A[0]) or n != len(B[0]):
        return -1
    if any((obs[i] < 0 for i in range(len(obs)))):
        return -1
    alpha_t = np.zeros(n)
    alpha_t[S[t]] = 1
    for t in range(1, t + 1):
        alpha_t = np.dot(alpha_t.T, A).dot(B[:, obs[t - 1]])
    return round(alpha_t, 4)
