from collections import deque
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    distances = np.sqrt(np.sum((data[:, np.newaxis] - data[np.newaxis, :]) ** 2, axis=2))
    neighborhoods = {}
    for i in range(data.shape[0]):
        for j in range(i + 1, data.shape[0]):
            if np.linalg.norm(data[i] - data[j]) < eps:
                if (i, j) not in neighborhoods:
                    neighborhoods[i, j] = []
                neighborhoods[i, j].append(i)
                neighborhoods[j, i].append(j)
    core_points = [i for i in range(data.shape[0]) if len(neighborhoods.get((i, i), [])) >= min_samples]
    cluster_labels = [-1] * data.shape[0]
    while core_points:
        core_point = core_points.pop(0)
        cluster_labels[core_point] = len(cluster_labels)
        for neighbor in neighborhoods[core_point, core_point]:
            if cluster_labels[neighbor] == -1:
                core_points.append(neighbor)
                cluster_labels[neighbor] = len(cluster_labels)
    return cluster_labels
