import math
import heapq
def k_nearest_neighbors(points: list[list[float]], query: list[float], k: int=3) -> list[int]:
    """Find the *k* nearest neighbours of *query* among *points* using a k-d tree.

    Parameters
    ----------
    points : list[list[float]]
        A list containing *n* points.  Each point itself is a list of floats
        and all points have the same dimensionality.
    query : list[float]
        The query point whose neighbours are to be found.
    k : int, default 3
        The number of nearest neighbours that should be returned.  If *k* is
        greater than the number of points, all points must be returned.

    Returns
    -------
    list[int]
        A list with the indices (0-based) of the *k* closest points, sorted by
        increasing distance to *query* (ties are broken by the smaller index).
    """

    def build_tree(points):
        if not points:
            return None
        root = {'index': -1, 'axis': None, 'left': None, 'right': None}
        for (i, point) in enumerate(points):
            insert(root, point)
        return root

    def insert(node, point):
        if node['axis'] is None:
            node['index'] = i
            node['axis'] = len(points[0])
            node['left'] = None
            node['right'] = None
        elif point[node['axis']] > node['axis']:
            if node['right'] is None:
                node['right'] = {'index': -1, 'axis': None, 'left': None, 'right': None}
            insert(node['right'], point)
        else:
            if node['left'] is None:
                node['left'] = {'index': -1, 'axis': None, 'left': None, 'right': None}
            insert(node['left'], point)

    def distance(point1, point2):
        return math.sqrt((point1[0] - point2[0]) ** 2 + (point1[1] - point2[1]) ** 2)

    def nearest_k_points(node, query, k):
        if node is None:
            return []
        if k == 0:
            return []
        if k == 1:
            return [node['index']]
        if k < 0:
            raise ValueError('k must be non-negative')
        if node['axis'] is None:
            return []
        if distance(query, node['point']) <= distance(query, node['left']):
            return nearest_k_points(node['left'], query, k)
        elif distance(query, node['point']) <= distance(query, node['right']):
            return nearest_k_points(node['right'], query, k)
        else:
            left_k = nearest_k_points(node['left'], query, k - 1)
            right_k = nearest_k_points(node['right'], query, k - 1)
            return left_k + right_k
    root = build_tree(points)
    return nearest_k_points(root, query, k)
