import numpy as np
def spectral_clustering(data: np.ndarray, n_cluster: int, gamma: float=2.0, method: str='unnormalized') -> list[int]:
    D = np.sum(data ** 2, axis=1)
    W = np.exp(-gamma * D)
    if method == 'normalized':
        D_inv_sqrt_2 = np.linalg.inv(D / 2)
        L = D_inv_sqrt_2 @ D_inv_sqrt_2
    else:
        L = D
    (eigvals, eigvecs) = np.linalg.eig(L)
    centroids = eigvecs[:, 0]
    labels = np.argmin(np.linalg.norm(data - centroids, axis=1), axis=0)
    return labels.tolist()

def predict_sample(root: 'Node', features: list | tuple, task_type: str) -> int:
    if root.y is not None:
        return root.y
    min_x = float('inf')
    min_node = None
    for child in root.child:
        if child.x < min_x:
            min_x = child.x
            min_node = child
    if min_node.is_leaf():
        return min_node.y
    return predict_sample(min_node, features, task_type)

