def statistic_key_word(data: List[Tuple[Any, Iterable[str]]], cut_off: int | None=None) -> Tuple[List[Tuple[str, float]], Set[str]]:
    word_counts = Counter()
    for (label, words) in data:
        word_counts.update(words)
    sorted_words = sorted(word_counts.items(), key=lambda item: item[1], reverse=True)
    if cut_off is None:
        return sorted_words
