from collections import defaultdict
def naive_bayes_predict(train_data: list[tuple[str, list[str]]], keywords: set[str], test_samples: list[list[str]]) -> list[str]:
    word_counts = defaultdict(int)
    for (label, tokens) in train_data:
        for token in tokens:
            if token in keywords:
                word_counts[token] += 1
    class_counts = defaultdict(int)
    for (label, tokens) in train_data:
        for token in tokens:
            if token in keywords:
                class_counts[label] += 1
    predictions = []
    for tokens in test_samples:
        max_probability = -float('inf')
        predicted_label = None
        for keyword in keywords:
            if keyword in word_counts:
                probability_present = word_counts[keyword] / len(tokens)
            else:
                probability_present = 0
            if keyword in class_counts:
                probability_absent = class_counts[keyword] / len(tokens)
            else:
                probability_absent = 0
            conditional_probability = probability_present * (1 - probability_absent)
            if conditional_probability > max_probability:
                max_probability = conditional_probability
                predicted_label = keyword
        predictions.append(predicted_label)
    return predictions
