import numpy as np
def kmeans(X: np.ndarray, k: int, max_iters: int=100) -> tuple[list[int], list[list[float]]]:
    if k <= 0 or k > X.shape[0]:
        raise ValueError('Number of clusters must be between 1 and the number of data points.')
    centers = X[np.random.choice(X.shape[0], k, replace=False)]
    for _ in range(max_iters):
        labels = np.argmin(np.linalg.norm(X - centers, axis=1), axis=1)
        new_centers = np.mean(X[labels], axis=0)
        if np.allclose(centers, new_centers):
            break
        centers = new_centers
    return (labels, centers.tolist())
