def neural_net_predict(weights: list[list[list[float]]], x: list[float]) -> float | list[float]:
    if len(weights) == 1:
        return round(weights[0][0][0], 4)
    prediction = weights[-1][0][0]
    for i in range(len(weights) - 2, -1, -1):
        prediction += weights[i][0][0] * x[i]
        prediction = np.maximum(0, prediction)
    return round(prediction, 4)
