def analyze_cell(clicked: np.ndarray, flags: np.ndarray, row: int, col: int) -> tuple[list[tuple[int, int]], int]:
    unopened_neighbours = []
    for i in range(row - 1, row + 2):
        for j in range(col - 1, col + 2):
            if i >= 0 and i < len(clicked) and (j >= 0) and (j < len(clicked[0])):
                if not clicked[i][j] and (not flags[i][j]):
