import numpy as np
import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:

    def calculate_error(y_true, y_pred):
        return sum(y_true != y_pred) / len(y_true)

    def train_stump(X, y, alpha):
        (n_samples, n_features) = X.shape
        best_error = float('inf')
        best_feature = -1
        best_threshold = None
        best_polarity = None
        for feature in range(n_features):
            for threshold in np.unique(X[:, feature]):
                predictions = np.sign(np.dot(X[:, feature], np.ones(n_samples)) - threshold)
                error = calculate_error(y, predictions)
                if error < best_error:
                    best_error = error
                    best_feature = feature
                    best_threshold = threshold
                    best_polarity = 1 if np.mean(predictions[y == 1]) > 0 else -1
        if best_error == 0:
            return (best_error, best_feature, best_threshold, best_polarity)
        w = np.exp(-alpha * y * best_polarity * (np.dot(X[:, best_feature], np.ones(n_samples)) - best_threshold))
        w /= np.sum(w)
        return (best_error, best_feature, best_threshold, best_polarity, w)

    def ada_boost(X, y, n_estimators):
        (n_samples, n_features) = X.shape
        n_classes = len(set(y))
        w = np.full(n_samples, 1 / n_samples)
        predictions = np.zeros(n_samples)
        for _ in range(n_estimators):
            (best_error, best_feature, best_threshold, best_polarity, w) = train_stump(X, y, w)
            if best_error == 0:
                break
            predictions += best_polarity * w
        return np.sign(predictions)
    return ada_boost(X_test, y_train, n_estimators)
