import numpy as np
def selu(x, order: int=0):
    """Compute the SELU activation or its derivatives.

    Parameters
    ----------
    x : float | list | np.ndarray
        Input value(s). Can be a scalar, 1-D list/array, or multi-D list/array.
    order : int, default 0
        0 → SELU(x)
        1 → first derivative d(SELU)/dx
        2 → second derivative d²(SELU)/dx²

    Returns
    -------
    list | float
        A Python list (or scalar for scalar input) containing the element-wise
        result, rounded to 4 decimals.
    """
    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    if isinstance(x, (int, float)):
        if order == 0:
            return round(scale * np.maximum(0, x), 4)
        elif order == 1:
            return round(scale * np.where(x > 0, 1, alpha * np.exp(x) - alpha), 4)
        elif order == 2:
            return round(scale * np.where(x > 0, 1, alpha * np.exp(x)), 4)
    elif isinstance(x, np.ndarray):
        if order == 0:
            return np.round(scale * np.maximum(0, x), 4)
        elif order == 1:
            return np.round(scale * np.where(x > 0, 1, alpha * np.exp(x) - alpha), 4)
        elif order == 2:
            return np.round(scale * np.where(x > 0, 1, alpha * np.exp(x)), 4)
    else:
        raise ValueError('Input must be a scalar, 1D list/array, or multi-D list/array')
