import numpy as np
import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def calculate_sse(y_true, y_pred):
        return np.sum((y_true - y_pred) ** 2)

    def find_best_split(X, y, max_depth):
        if len(y) <= 1:
            return (None, 0)
        best_feature = None
        best_threshold = None
        min_sse = float('inf')
        for feature in range(len(X[0])):
            sorted_indices = np.argsort(X[:, feature])
            for i in range(1, len(sorted_indices)):
                threshold = (X[sorted_indices[i], feature] + X[sorted_indices[i - 1], feature]) / 2
                left_y = y[sorted_indices[:i]]
                right_y = y[sorted_indices[i:]]
                sse_left = calculate_sse(left_y, np.mean(left_y))
                sse_right = calculate_sse(right_y, np.mean(right_y))
                total_sse = sse_left + sse_right
                if total_sse < min_sse:
                    min_sse = total_sse
                    best_feature = feature
                    best_threshold = threshold
        return (best_feature, best_threshold)

    def build_tree(X, y, depth=0):
        if depth >= max_depth or len(y) <= 1:
            return np.mean(y)
        (best_feature, best_threshold) = find_best_split(X, y, max_depth)
        if best_feature is None:
            return np.mean(y)
        left_mask = X[:, best_feature] <= best_threshold
        right_mask = X[:, best_feature] > best_threshold
        left_tree = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_tree = build_tree(X[right_mask], y[right_mask], depth + 1)
        return (best_feature, best_threshold, left_tree, right_tree)

    def predict(tree, x):
        if isinstance(tree, float):
            return tree
        (feature, threshold, left_tree, right_tree) = tree
        if x[feature] <= threshold:
            return predict(left_tree, x)
        else:
            return predict(right_tree, x)
    tree = build_tree(np.array(X_train), np.array(y_train), max_depth)
    predictions = [predict(tree, x) for x in X_test]
    return predictions
