def map_agent_environment(agent_name: str):
    """Return the environment name and access type that should be used for a given RL agent.

    The mapping is case-insensitive and ignores leading/trailing spaces. If the
    agent name is not recognised, the function returns -1.

    Args:
        agent_name: Name of the RL agent (e.g. "dqn", "A3C", etc.).

    Returns:
        Tuple[str, str] if the agent is known, or -1 otherwise.
    """
    agent_name = agent_name.strip().lower()
    if agent_name == 'dqn':
        return ('cartpole-v0', 'unwrapped')
    elif agent_name == 'ddpg':
        return ('pendulum-v0', 'env')
    elif agent_name == 'a3c':
        return ('pendulum-v0', 'unwrapped')
    elif agent_name == 'a2c':
        return ('cartpole-v0', 'env')
    elif agent_name == 'a2c_multi':
        return ('cartpole-v0', 'raw')
    elif agent_name == 'trpo':
        return ('pendulum-v0', 'unwrapped')
    else:
        return -1
assert map_agent_environment("dqn") == ("CartPole-v0", "unwrapped"), "failed on dqn"
assert map_agent_environment("Ddpg") == ("Pendulum-v0", "env"), "failed on Ddpg (case-insensitive)"
