assert map_agent_environment("A2C_MULTI") == ("CartPole-v0", "raw"), "failed on A2C_MULTI"
assert map_agent_environment("trpo") == ("Pendulum-v0", "unwrapped"), "failed on trpo"
assert map_agent_environment("random_agent") == -1, "failed on unknown agent"
assert map_agent_environment("  dqn  ") == ("CartPole-v0", "unwrapped"), "failed on whitespace handling"
assert map_agent_environment("") == -1, "failed on empty string"
assert map_agent_environment("A2c") == ("CartPole-v0", "env"), "failed on mixed-case A2c"

class Node:

    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

    def __gt__(self, other):
        return self.val > other.val

    def __ge__(self, other):
        return self.val >= other.val

    def __lt__(self, other):
        return self.val < other.val

    def __le__(self, other):
        return self.val <= other.val
def inorder_keys(pairs):
    if not pairs:
        return []

    def build_bst(node, pairs):
        if not pairs:
            return node
        (key, val) = pairs.pop(0)
        if val > node.val:
            node.right = build_bst(Node(val), pairs)
        else:
            node.left = build_bst(Node(val), pairs)
        return node
    root = build_bst(Node(pairs[0][1]), pairs)

    def in_order_traversal(node, result):
        if node is None:
            return
        in_order_traversal(node.left, result)
        result.append(node.val)
        in_order_traversal(node.right, result)
    result = []
    in_order_traversal(root, result)
    return result
assert inorder_keys([('a', 3), ('b', 1), ('c', 5), ('d', 2)]) == ['b', 'd', 'a', 'c'], "test case failed: [('a',3),('b',1),('c',5),('d',2)]"
