from typing import Dict
def update_kernel_settings(parameters: dict, hyperparameters: dict, summary_dict: dict) -> tuple[dict, dict]:
    """Update kernel parameters and hyperparameters.

    The function must not modify the input dictionaries.  It should return two
    *new* dictionaries in which the supplied `summary_dict` updates have been
    applied following the rules explained in the task description.

    Args:
        parameters (dict):      Existing kernel parameters.
        hyperparameters (dict): Existing kernel hyperparameters.
        summary_dict (dict):    Dictionary that may contain the keys
            "parameters" and/or "hyperparameters" whose values are dictionaries
            of updates.  Any top-level keys are also treated as updates.

    Returns:
        tuple[dict, dict]: `(new_parameters, new_hyperparameters)` – the
        updated dictionaries.
    """
    new_parameters = {**parameters}
    new_hyperparameters = {**hyperparameters}
    for (key, value) in summary_dict.items():
        if key == 'parameters':
            new_parameters.update(value)
        elif key == 'hyperparameters':
            new_hyperparameters.update(value)
    return (new_parameters, new_hyperparameters)
# Basic update of both dictionaries
params = {"sigma": 1, "gamma": 0.5}
hparams = {"id": "RBF", "trainable": True}
summary = {"parameters": {"sigma": 2}, "hyperparameters": {"trainable": False}}
assert update_kernel_settings(params, hparams, summary) == ( {"sigma": 2, "gamma": 0.5}, {"id": "RBF", "trainable": False} ), "test 1 failed"
# Only parameter update
params = {"a": 10, "b": 20}
hparams = {"id": "LIN"}
summary = {"parameters": {"a": 15}}
assert update_kernel_settings(params, hparams, summary) == ( {"a": 15, "b": 20}, {"id": "LIN"} ), "test 2 failed"
# Only hyperparameter update
params = {"c": 3}
hparams = {"id": "POLY", "degree": 2}
summary = {"hyperparameters": {"degree": 3}}
assert update_kernel_settings(params, hparams, summary) == ( {"c": 3}, {"id": "POLY", "degree": 3} ), "test 3 failed"
# Mixed nested and flat updates
params = {"p": 1, "q": 2}
hparams = {"id": "MIX", "flag": False}
summary = {"parameters": {"p": 7}, "flag": True}
assert update_kernel_settings(params, hparams, summary) == ( {"p": 7, "q": 2}, {"id": "MIX", "flag": True} ), "test 4 failed"
# Unknown keys are ignored
params = {"x": 0}
hparams = {"id": "UNK"}
summary = {"parameters": {"y": 99}, "foo": 123}
assert update_kernel_settings(params, hparams, summary) == ( {"x": 0}, {"id": "UNK"} ), "test 5 failed"
# Original dictionaries remain unchanged
params = {"u": 4}
hparams = {"id": "ORI"}
summary = {"parameters": {"u": 5}}
update_kernel_settings(params, hparams, summary)
assert params == {"u": 4} and hparams == {"id": "ORI"}, "test 6 failed"
# Empty summary dictionary
params = {"a": 1}
hparams = {"id": "EMPTY"}
summary = {}
assert update_kernel_settings(params, hparams, summary) == ( {"a": 1}, {"id": "EMPTY"} ), "test 7 failed"
# Flattening removes nested keys influence on iteration order
params = {"k1": 1}
hparams = {"id": "X"}
summary = {"parameters": {"k1": 2}, "hyperparameters": {"id": "Y"}}
assert update_kernel_settings(params, hparams, summary) == ( {"k1": 2}, {"id": "Y"} ), "test 8 failed"
# Summary provides direct key also present in nested dict
params = {"alpha": 0.1}
hparams = {"id": "ALPHA", "beta": 0.2}
summary = {"parameters": {"alpha": 0.3}, "alpha": 0.4, "beta": 0.25}
assert update_kernel_settings(params, hparams, summary) == ( {"alpha": 0.4}, {"id": "ALPHA", "beta": 0.25} ), "test 9 failed"
# Large dictionary stress test
params = {str(i): i for i in range(100)}
hparams = {"id": "BIG", **{f"h{i}": i for i in range(100)}}
summary = {"parameters": {"0": -1, "50": -50}, "hyperparameters": {"h99": -99}}
assert update_kernel_settings(params, hparams, summary)[0]["0"] == -1 and update_kernel_settings(params, hparams, summary)[1]["h99"] == -99, "test 10 failed"

import heapq
import heapq
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    if not tokens:
        return {}
    freq = {}
    for token in tokens:
        if token in freq:
            freq[token] += 1
        else:
            freq[token] = 1
    heap = []
    for (token, count) in freq.items():
        node = {'token': token, 'frequency': count, 'left': None, 'right': None}
        heapq.heappush(heap, node)
    while len(heap) > 1:
        left_node = heapq.heappop(heap)
        right_node = heapq.heappop(heap)
        parent = {'token': '', 'frequency': left_node['frequency'] + right_node['frequency'], 'left': left_node, 'right': right_node}
        heapq.heappush(heap, parent)

    def traverse(node, code=''):
        if node['left']:
            traverse(node['left'], code + '0')
        if node['right']:
            traverse(node['right'], code + '1')
        if node['token']:
            result[node['token']] = code
    result = {}
    traverse(heapq.heappop(heap))
    return result
tokens = ['apple', 'banana', 'cherry']
assert huffman_encode([]) == {}, "failed on empty input"
assert huffman_encode(["a"]) == {"a": "0"}, "failed on single token"
assert huffman_encode(["a", "b"]) == {"a": "0", "b": "1"}, "failed on two tokens"
assert huffman_encode(["a", "b", "a", "b"]) == {"a": "0", "b": "1"}, "failed on uniform frequencies"
