from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """
    if isinstance(env.action_space, Box) and tuple_action:
        return (True, all((isinstance(sub_space, Box) for sub_space in env.action_space.spaces)))
    elif isinstance(env.action_space, (Tuple, Dict)) and tuple_action:
        return (True, all((isinstance(sub_space, Box) for sub_space in env.action_space.spaces)))
    elif isinstance(env.action_space, Box) and (not tuple_action):
        return (True, True)
    else:
        return (False, False)
assert is_continuous(Env(Box(-1.0, 1.0, (3,)), Box(-1.0, 1.0, (3,))), False, False) == (True, True), "TC1 failed"
assert is_continuous(Env(Discrete(4), Discrete(4)), False, False) == (False, False), "TC2 failed"
assert is_continuous(Env(Box(-1.0, 1.0, (3,)), Discrete(4)), False, False) == (True, False), "TC3 failed"
assert is_continuous(Env(Tuple([Box(-1.0,1.0,(3,)), Box(-1.0,1.0,(3,))]), Box(-1.0,1.0,(3,))), True, False) == (True, True), "TC4 failed"
assert is_continuous(Env(Tuple([Box(-1.0,1.0,(3,)), Discrete(5)]), Box(-1.0,1.0,(3,))), True, False) == (False, True), "TC5 failed"
assert is_continuous(Env(Box(-1.0,1.0,(3,)), Dict({"a": Box(-1.0,1.0,(3,)), "b": Box(-1.0,1.0,(3,))})), False, True) == (True, True), "TC6 failed"
assert is_continuous(Env(Box(-1.0,1.0,(3,)), Dict({"a": Box(-1.0,1.0,(3,)), "b": Discrete(2)})), False, True) == (True, False), "TC7 failed"
assert is_continuous(Env(Tuple([]), Box(-1.0,1.0,(3,))), True, False) == (True, True), "TC8 failed"
assert is_continuous(Env(Tuple([Box(-1.0,1.0,(3,)) for _ in range(10)]), Discrete(5)), True, False) == (True, False), "TC9 failed"
assert is_continuous(Env(Discrete(2), Box(-1.0,1.0,(3,))), False, False) == (False, True), "TC10 failed"

import re
import re
import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]) -> None:
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, str):
        match = re.match('(Linear|Polynomial|RBF)\\(([^)]+)\\)', param)
        if match:
            name = match.group(1).lower()
            params = match.group(2)
            if name == 'linear':
                return LinearKernel()
            elif name == 'polynomial':
                params_dict = eval(params)
                return PolynomialKernel(**params_dict)
            elif name == 'rbf':
                params_dict = eval(params)
                return RBFKernel(**params_dict)
            else:
                raise NotImplementedError(f'Unknown kernel: {name}')
        else:
            raise ValueError('Invalid string representation')
    if isinstance(param, dict):
        hyperparameters = param.get('hyperparameters', {})
        if 'id' not in hyperparameters:
            raise ValueError("Dictionary must contain 'hyperparameters' entry")
        name = hyperparameters['id'].lower()
        if name == 'linear':
            return LinearKernel()
        elif name == 'polynomial':
            params_dict = hyperparameters.copy()
            del params_dict['id']
            return PolynomialKernel(**params_dict)
        elif name == 'rbf':
            params_dict = hyperparameters.copy()
            del params_dict['id']
            return RBFKernel(**params_dict)
        else:
            raise NotImplementedError(f'Unknown kernel: {name}')
    raise ValueError('Unsupported input format')
assert isinstance(initialize_kernel(), LinearKernel), "Test failed: None should give LinearKernel"
lin = LinearKernel(); assert initialize_kernel(lin) is lin, "Test failed: Existing instance should be returned unchanged"
assert isinstance(initialize_kernel("linear"), LinearKernel), "Test failed: 'linear' string"
rbf = initialize_kernel("RBF(gamma=0.5)"); assert isinstance(rbf, RBFKernel) and abs(rbf.params["gamma"] - 0.5) < 1e-12, "Test failed: RBF with gamma"
poly = initialize_kernel("Polynomial(degree=4, coef0=2)"); assert isinstance(poly, PolynomialKernel) and poly.params == {"degree": 4, "coef0": 2}, "Test failed: Polynomial with kwargs"
d1 = {"hyperparameters": {"id": "LinearKernel"}}; assert isinstance(initialize_kernel(d1), LinearKernel), "Test failed: dict → LinearKernel"
d2 = {"hyperparameters": {"id": "PolynomialKernel", "degree": 5}}; poly2 = initialize_kernel(d2); assert isinstance(poly2, PolynomialKernel) and poly2.params["degree"] == 5, "Test failed: dict → PolynomialKernel"
d3 = {"hyperparameters": {"id": "RBFKernel", "gamma": 2.5}}; rbf2 = initialize_kernel(d3); assert isinstance(rbf2, RBFKernel) and rbf2.params["gamma"] == 2.5, "Test failed: dict → RBFKernel"
assert isinstance(initialize_kernel("rbf"), RBFKernel), "Test failed: 'rbf' string"
assert isinstance(initialize_kernel("poly"), PolynomialKernel), "Test failed: 'poly' shorthand"

import numpy as np
import numpy as np
def selu(x, order: int=0):
    """Compute the SELU activation or its derivatives.

    Parameters
    ----------
    x : float | list | np.ndarray
        Input value(s). Can be a scalar, 1-D list/array, or multi-D list/array.
    order : int, default 0
        0 → SELU(x)
        1 → first derivative d(SELU)/dx
        2 → second derivative d²(SELU)/dx²

    Returns
    -------
    list | float
        A Python list (or scalar for scalar input) containing the element-wise
        result, rounded to 4 decimals.
    """
    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    if isinstance(x, (int, float)):
        if order == 0:
            return round(scale * np.maximum(0, x), 4)
        elif order == 1:
            return round(scale * np.where(x > 0, 1, alpha * np.exp(x) - alpha), 4)
        elif order == 2:
            return round(scale * np.where(x > 0, 1, alpha * np.exp(x)), 4)
    elif isinstance(x, np.ndarray):
        if order == 0:
            return np.round(scale * np.maximum(0, x), 4)
        elif order == 1:
            return np.round(scale * np.where(x > 0, 1, alpha * np.exp(x) - alpha), 4)
        elif order == 2:
            return np.round(scale * np.where(x > 0, 1, alpha * np.exp(x)), 4)
    else:
        raise ValueError('Input must be a scalar, 1D list/array, or multi-D list/array')
