import numpy as np
import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:

    def find_neighbors(point, data):
        neighbors = []
        for (i, d) in enumerate(data):
            if np.linalg.norm(d - point) <= eps:
                neighbors.append(i)
        return neighbors

    def expand_cluster(point, neighbors, visited, cluster_id):
        visited.add(point)
        cluster[point] = cluster_id
        for neighbor in neighbors:
            if neighbor not in visited:
                cluster[neighbor] = cluster_id
                new_neighbors = find_neighbors(data[neighbor], data)
                if len(new_neighbors) >= min_samples:
                    expand_cluster(neighbor, new_neighbors, visited, cluster_id)
    n_samples = data.shape[0]
    visited = set()
    cluster = {}
    cluster_id = 0
    for i in range(n_samples):
        if i in visited:
            continue
        neighbors = find_neighbors(data[i], data)
        if len(neighbors) < min_samples:
            cluster[i] = -1
        else:
            cluster_id += 1
            expand_cluster(i, neighbors, visited, cluster_id)
    return list(cluster.values())
