def k_nearest_neighbors(points: list[list[float]], query: list[float], k: int=3) -> list[int]:

    def insert(node, point, depth=0):
        if not node:
            return {'index': len(points), 'split_axis': depth % len(point), 'left': None, 'right': None}
        split_axis = node['split_axis']
        value = point[split_axis]
        if value < node['value']:
            node['left'] = insert(node['left'], point, depth + 1)
        else:
            node['right'] = insert(node['right'], point, depth + 1)
        return node

    def search(node, query, depth=0, results=[]):
        if not node:
            return
        split_axis = node['split_axis']
        value = query[split_axis]
        if value == node['value']:
            dist = sum(((a - b) ** 2 for (a, b) in zip(query, node['point'])))
            if dist <= 1e-06:
                results.append(node['index'])
            search(node['left'], query, depth + 1, results)
            search(node['right'], query, depth + 1, results)
        elif value < node['value']:
            search(node['left'], query, depth + 1, results)
        else:
            search(node['right'], query, depth + 1, results)

    def build_tree(points):
        root = None
        for (i, point) in enumerate(points):
            root = insert(root, point)
        return root

    def get_distance(point1, point2):
        return sum(((a - b) ** 2 for (a, b) in zip(point1, point2)))
    root = build_tree(points)
    results = []
    search(root, query, 0, results)
    results.sort(key=lambda x: (get_distance(points[x], query), x))
    return results[:k]
