import random
import random
from typing import Any, List, Tuple
import random
def manage_replay_memory(operations: List[Tuple]) -> List[Any]:
    """Perform operations on an in-memory replay buffer.

    The buffer starts out empty and is manipulated according to *operations*.

    Args:
        operations: A list of tuples describing the commands to execute. The
            first element of each tuple is a string ("append", "pop", "sample",
            or "size").  Additional elements contain the arguments of that
            command.

    Returns:
        A list containing the outputs of all commands that generate a result (in their original order).
    """
    random.seed(0)
    replay_memory = []
    for (cmd, arg) in operations:
        if cmd == 'append':
            replay_memory.append(arg)
        elif cmd == 'pop':
            if replay_memory:
                return replay_memory.pop()
            else:
                return None
        elif cmd == 'sample':
            if replay_memory:
                return random.sample(replay_memory, int(arg))
            else:
                return []
        elif cmd == 'size':
            return len(replay_memory)
        else:
            raise ValueError(f'Unknown command: {cmd}')
    return []
assert manage_replay_memory([('append', 1), ('append', 2), ('sample', 1), ('pop',), ('size',)]) == [[2], 2, 1], "test case failed: basic mixed operations"
assert manage_replay_memory([('size',)]) == [0], "test case failed: size on empty buffer"
assert manage_replay_memory([('pop',)]) == [None], "test case failed: pop on empty buffer"
assert manage_replay_memory([('append', 'a'), ('sample', 5)]) == [[]], "test case failed: oversampling returns empty list"
assert manage_replay_memory([('append', 'first'), ('append', 'second'), ('pop',)]) == ['second'], "test case failed: LIFO pop behaviour"
assert manage_replay_memory([('append', 1), ('append', 2), ('sample', 1), ('size',)]) == [[2], 2], "test case failed: sample does not change size"
assert manage_replay_memory([('append', 100), ('pop',), ('pop',)]) == [100, None], "test case failed: pop returns None on second call"
assert manage_replay_memory([('append', 'x'), ('append', 'y'), ('append', 'z'), ('size',), ('pop',), ('size',)]) == [3, 'z', 2], "test case failed: size before and after pop"
assert manage_replay_memory([('sample', 0), ('append', 5), ('sample', 0), ('size',)]) == [[], [], 1], "test case failed: zero-sized sampling"
