import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity(μ):
        return μ

    def inv_identity(η):
        return η

    def log(μ):
        return np.log(μ)

    def inv_log(η):
        return np.exp(η)

    def logit(μ):
        return np.log(μ / (1 - μ))

    def inv_logit(η):
        return 1 / (1 + np.exp(-η))
    return {'identity': {'link': identity, 'inv_link': inv_identity, 'derivative': lambda μ: np.ones_like(μ)}, 'log': {'link': log, 'inv_link': inv_log, 'derivative': lambda μ: 1 / μ}, 'logit': {'link': logit, 'inv_link': inv_logit, 'derivative': lambda μ: 1 / (1 - μ)}}

import numpy as np
def l2_distance(X: np.ndarray) -> list[list[float]]:
    if len(X) == 0:
        return []
    n_samples = len(X)
    distances = np.zeros((n_samples, n_samples))
    norms = np.linalg.norm(X, axis=1)
    for i in range(n_samples):
        for j in range(i + 1, n_samples):
            distances[i][j] = norms[i] ** 2 + norms[j] ** 2 - 2 * np.dot(X[i], X[j])
    distances += distances.T
    distances_rounded = np.round(distances, 4)
    return distances_rounded.tolist()

def build_tree(items: list[str], counts: list[int], parents: list[int]) -> dict:
    """Construct a rooted tree from parallel *items*, *counts* and *parents* lists.

    Parameters
    ----------
    items : list[str]
        The label (item) stored in each node.
    counts : list[int]
        The frequency (count) stored in each node.
    parents : list[int]
        The parent index of each node; –1 marks the single root.

    Returns
    -------
    dict
        The root node represented as a nested dictionary with keys
        'item', 'count' and 'children'.  If the inputs are empty, returns
        an empty dictionary.
    """
    if not items or not counts or (not parents):
        return {}
    tree = {}
    node_index_map = {i: idx for (idx, i) in enumerate(items)}
    for (i, parent) in enumerate(parents):
        if parent == -1:
            tree['root'] = {'item': items[i], 'count': counts[i], 'children': []}
            continue
        child_node = {'item': items[i], 'count': counts[i], 'children': []}
        if 'root' not in tree:
            tree['root'] = child_node
        else:
            current_node = tree['root']
            while current_node['parent'] != -1:
                current_node = tree[current_node['parent']]
            current_node['children'].append(child_node)
    return tree

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings.  Any string
    should be converted with `float()`; existing numeric values stay as they
    are.  The pair `(a, b)` must then be returned.
    """
    try:
        if isinstance(a, str):
            a = float(a)
        if isinstance(b, str):
            b = float(b)
    except ValueError:
        raise ValueError('Both arguments must be numeric.')
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
