from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node:   rule = (feature_index, threshold)
    Leaf node:       rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Predicts the class labels for each sample using the provided decision tree."""
    predictions = []
    for sample in X:
        current_node = root
        while current_node is not None:
            if current_node.feature is None:
                predictions.append(current_node.threshold)
                break
            elif sample[current_node.feature] <= current_node.threshold:
                current_node = current_node.left
            else:
                current_node = current_node.right
    return predictions

from typing import List, Union
Number = Union[int, float]
def k_smallest(nums: List[Number], k: int) -> List[Number]:
    """Return the k smallest numbers contained in *nums*.

    The function should scan the input list once, maintaining a custom
    max-heap (size at most k) implemented with a Python list.  At the end,
    return the k smallest elements in ascending order.  If *k* is invalid
    (≤0 or larger than len(nums)) return -1.

    The use of the built-in ``heapq`` module is **not** allowed.
    """
    if not nums or k <= 0:
        return -1

    def _move_up(index):
        while index > 0 and nums[index] < nums[(index - 1) // 2]:
            (nums[index], nums[(index - 1) // 2]) = (nums[(index - 1) // 2], nums[index])
            index = (index - 1) // 2

    def _move_down(index):
        left_child_index = 2 * index + 1
        right_child_index = 2 * index + 2
        while left_child_index < len(nums):
            smallest_child_index = left_child_index
            if right_child_index < len(nums) and nums[right_child_index] < nums[left_child_index]:
                smallest_child_index = right_child_index
            if nums[index] > nums[smallest_child_index]:
                (nums[index], nums[smallest_child_index]) = (nums[smallest_child_index], nums[index])
                index = smallest_child_index
            else:
                break
    for num in nums:
        if len(nums) < k:
            nums.append(num)
            _move_up(len(nums) - 1)
        elif num < nums[0]:
            nums[0] = num
            _move_down(0)
    nums.sort()
    return nums[:k]

import numpy as np
def softplus(x, deriv: bool=False):
    """
    Computes the SoftPlus activation function or its gradient.

    Args:
        x (float | list | np.ndarray): The input data.
        deriv (bool): If True, compute the gradient; otherwise, compute the SoftPlus function.

    Returns:
        float | list | np.ndarray: The result of the SoftPlus function or its gradient.
    """
    if isinstance(x, (list, np.ndarray)):
        x = [float(i) for i in x]
        if deriv:
            return [1 / (1 + np.exp(-i)) for i in x]
        else:
            return [np.log1p(np.exp(i)) for i in x]
    if deriv:
        return 1 / (1 + np.exp(-x))
    else:
        return np.log1p(np.exp(x))

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}

    def build_fp_tree(frequent_items):
        root = {}
        for transaction in transactions:
            current_node = root
            for item in sorted(transaction):
                if item not in current_node:
                    current_node[item] = {}
                current_node = current_node[item]
        return root
    fp_tree = build_fp_tree(frequent_items)

    def mine_conditional_fptree(node, prefix, frequent_itemsets):
        if len(prefix) > 0:
            frequent_itemsets.append(prefix)
        if node:
            for (item, child) in node.items():
                mine_conditional_fptree(child, prefix + [item], frequent_itemsets)
    frequent_itemsets = []
    mine_conditional_fptree(fp_tree, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
