import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, int) or isinstance(Z, float):
        return max(0, Z)
    elif isinstance(Z, list):
        return [max(0, z) for z in Z]
    elif isinstance(Z, np.ndarray):
        return np.maximum(0, Z)
    else:
        raise TypeError('Input must be a scalar, list, or numpy.ndarray.')

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Converts a raw text string into a fixed-length list of integer token ids."""
    indices = []
    for char in text[:time_steps]:
        if char in vocabulary:
            indices.append(vocabulary[char])
        else:
            indices.append(0)
    return indices
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list/array of integer ids back to text."""
    result = []
    for idx in indices:
        if idx != 0:
            result.append(inverse_vocab[idx])
    return ''.join(result)
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Runs the example sequence encoding-decoding pipeline."""
    indices = string_to_int(text, TIME_STEPS, input_vocabulary)
    predictions = model.predict(np.array([indices]))
    argmax_predictions = np.argmax(predictions, axis=-1)
    decoded_text = int_to_string(argmax_predictions[0], inv_output_vocabulary)
    return decoded_text

import numpy as np
def linear(z):
    """Linear (identity) activation function.

    Args:
        z: Scalar, list, tuple or NumPy ndarray.

    Returns:
        The same value(s) as *z*, obeying the rules:
            • Scalars are returned unchanged.
            • Non-scalar inputs are returned as a NumPy ndarray.
    """
    if isinstance(z, (list, tuple)):
        return np.array(z)
    elif isinstance(z, np.ndarray):
        return z.copy()
    else:
        return z

import numpy as np
def euclidean_distance(x1, x2):
    return np.linalg.norm(x1 - x2)
def manhattan_distance(x1, x2):
    return np.sum(np.abs(x1 - x2))
def cosine_similarity(x1, x2):
    dot_product = np.dot(x1, x2)
    norm_x1 = np.linalg.norm(x1)
    norm_x2 = np.linalg.norm(x2)
    return dot_product / (norm_x1 * norm_x2)
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """
    Implement your code here.

    Parameters:
    X_train (np.ndarray): Training data features.
    y_train (np.ndarray): Training data labels.
    X_test (np.ndarray): Test data features.
    k (int): Number of nearest neighbors to consider.
    metric (str): Distance metric ('euclidean', 'manhattan', or 'cosine').

    Returns:
    np.ndarray: Predicted labels for the test data.
    """
    if metric == 'euclidean':
        distance_func = euclidean_distance
    elif metric == 'manhattan':
        distance_func = manhattan_distance
    elif metric == 'cosine':
        distance_func = cosine_similarity
    else:
        raise ValueError("Unsupported metric. Please choose from 'euclidean', 'manhattan', or 'cosine'.")
    predicted_labels = np.zeros(X_test.shape[0])
    for (i, x_test) in enumerate(X_test):
        distances = [distance_func(x_test, x_train[j]) for j in range(X_train.shape[0])]
        sorted_indices = np.argsort(distances)[:k]
        label_counts = np.bincount(y_train[sorted_indices])
        predicted_label = label_counts.argmax()
        if len(label_counts) > 1 and label_counts[predicted_label] < label_counts[label_counts.argmin()]:
            predicted_label = label_counts.argmin()
        predicted_labels[i] = predicted_label
    return predicted_labels

import numpy as np
def knn_predict(X: np.ndarray, y: np.ndarray, X_test: np.ndarray, k: int=3, metric: str='euclidean') -> list:
    """Predict labels for *X_test* using the k-Nearest Neighbours algorithm.

    Args:
        X: 2-D NumPy array of shape (n_samples, n_features) containing the
           training features.
        y: 1-D NumPy array of length *n_samples* containing the training labels.
        X_test: 2-D NumPy array of shape (m_samples, n_features) with the test
                 samples whose labels are to be predicted.
        k: Number of neighbours to consider (default: 3).  If *k* exceeds the
           number of training samples, use all samples instead.
        metric: Distance metric to use – 'euclidean', 'manhattan', or 'cosine'.

    Returns:
        A Python list containing the predicted label for each test sample, in
        the same order as *X_test*.
    """

    def euclidean_distance(x, x_test):
        return np.sqrt(np.sum((x - x_test) ** 2))

    def manhattan_distance(x, x_test):
        return np.sum(np.abs(x - x_test))

    def cosine_similarity(x, x_test):
        dot_product = np.dot(x, x_test)
        norm_x = np.linalg.norm(x)
        norm_x_test = np.linalg.norm(x_test)
        if norm_x_test == 0:
            return 0
        return 1 - dot_product / (norm_x * norm_x_test)
    distances = []
    for x_test_sample in X_test:
        if metric == 'euclidean':
            distances.append(euclidean_distance(x_test_sample, X))
        elif metric == 'manhattan':
            distances.append(manhattan_distance(x_test_sample, X))
        elif metric == 'cosine':
            distances.append(cosine_similarity(x_test_sample, X))
    nearest_indices = np.argsort(distances)[:k]
    nearest_labels = y[nearest_indices]
    (unique_labels, counts) = np.unique(nearest_labels, return_counts=True)
    predicted_label = unique_labels[np.argmin(counts)]
    return [predicted_label] * len(X_test)

import numpy as np
def activation_forward_backward(X: np.ndarray, activation: str, upstream_grad: np.ndarray) -> tuple[np.ndarray, np.ndarray]:
    """Apply an activation function and compute its local gradient.

    Parameters
    ----------
    X : np.ndarray
        Raw pre-activation values.
    activation : str
        Name of the activation function. Supported values are
        "sigmoid", "tanh", "relu", and "softmax".
    upstream_grad : np.ndarray
        Gradient that arrives from the layer above (must have the same shape as
        `X`).

    Returns
    -------
    tuple[np.ndarray, np.ndarray]
        A two-element tuple `(A, grad)` where:
          • `A`    – the activation applied to `X`.
          • `grad` – the element-wise product of `upstream_grad` and the local
                      derivative of the activation (rounded to 4 decimals).
    """

    def sigmoid(x):
        return 1 / (1 + np.exp(-x))

    def tanh(x):
        return (np.exp(x) - np.exp(-x)) / (np.exp(x) + np.exp(-x))

    def relu(x):
        return np.maximum(0, x)

    def softmax(x):
        exp_x = np.exp(x - np.max(x, axis=-1, keepdims=True))
        return exp_x / np.sum(exp_x, axis=-1, keepdims=True)
    if activation == 'sigmoid':
        A = sigmoid(X)
        grad = upstream_grad * A * (1 - A)
    elif activation == 'tanh':
        A = tanh(X)
        grad = upstream_grad * (1 - A ** 2)
    elif activation == 'relu':
        A = relu(X)
        grad = upstream_grad * (A > 0)
    elif activation == 'softmax':
        A = softmax(X)
        grad = upstream_grad * A
    else:
        raise ValueError('Unsupported activation function')
    return (np.round(A, 4), np.round(grad, 4))

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:

    def calculate_error(y_true, y_pred):
        return sum(y_true != y_pred) / len(y_true)

    def train_stump(X, y, alpha):
        (n_samples, n_features) = X.shape
        best_error = float('inf')
        best_feature = -1
        best_threshold = None
        best_polarity = None
        for feature in range(n_features):
            for threshold in np.unique(X[:, feature]):
                predictions = np.sign(np.dot(X[:, feature], np.ones(n_samples)) - threshold)
                error = calculate_error(y, predictions)
                if error < best_error:
                    best_error = error
                    best_feature = feature
                    best_threshold = threshold
                    best_polarity = 1 if np.mean(predictions[y == 1]) > 0 else -1
        if best_error == 0:
            return (best_error, best_feature, best_threshold, best_polarity)
        w = np.exp(-alpha * y * best_polarity * (np.dot(X[:, best_feature], np.ones(n_samples)) - best_threshold))
        w /= np.sum(w)
        return (best_error, best_feature, best_threshold, best_polarity, w)

    def ada_boost(X, y, n_estimators):
        (n_samples, n_features) = X.shape
        n_classes = len(set(y))
        w = np.full(n_samples, 1 / n_samples)
        predictions = np.zeros(n_samples)
        for _ in range(n_estimators):
            (best_error, best_feature, best_threshold, best_polarity, w) = train_stump(X, y, w)
            if best_error == 0:
                break
            predictions += best_polarity * w
        return np.sign(predictions)
    return ada_boost(X_test, y_train, n_estimators)

import numpy as np
def gini_impurity(y):
    """Calculate the Gini impurity of a set."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def find_best_split(X, y, depth=0, max_depth=None):
    """Find the best split for the given dataset."""
    if depth >= max_depth or len(np.unique(y)) == 1:
        return {'class': np.bincount(y)[0], 'split': None}
    best_gain = -np.inf
    best_feature = None
    best_threshold = None
    unique_values = np.unique(X[:, best_feature])
    for i in range(len(unique_values) - 1):
        threshold = (unique_values[i] + unique_values[i + 1]) / 2
        left_y = y[X[:, best_feature] < threshold]
        right_y = y[X[:, best_feature] >= threshold]
        left_gini = gini_impurity(left_y)
        right_gini = gini_impurity(right_y)
        weight = len(left_y) / len(y)
        weighted_gini = weight * left_gini + (1 - weight) * right_gini
        if weighted_gini > best_gain:
            best_gain = weighted_gini
            best_feature = best_feature
            best_threshold = threshold
    return {'feature': best_feature, 'threshold': best_threshold, 'left': find_best_split(X[y <= best_threshold], y[y <= best_threshold], depth + 1, max_depth), 'right': find_best_split(X[y > best_threshold], y[y > best_threshold], depth + 1, max_depth)}
def build_tree(X, y, max_depth=None):
    """Build a decision tree recursively."""
    root = find_best_split(X, y, max_depth=max_depth)
    if root['split'] is None:
        return root
    left_child = build_tree(root['left']['X'], root['left']['y'], max_depth=root['left']['depth'])
    right_child = build_tree(root['right']['X'], root['right']['y'], max_depth=root['right']['depth'])
    return {'feature': root['feature'], 'threshold': root['threshold'], 'left': left_child, 'right': right_child}
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = build_tree(X_train, y_train, max_depth=max_depth)

    def predict_sample(sample):
        current_node = tree
        while current_node['split'] is not None:
            if sample[current_node['feature']] < current_node['threshold']:
                current_node = current_node['left']
            else:
                current_node = current_node['right']
        return current_node['class']
    return [predict_sample(sample) for sample in X_test]

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_input(x, y):
        if not isinstance(x, (list, tuple, np.ndarray)) or not isinstance(y, (list, tuple, np.ndarray)):
            return False
        if len(x) != len(y):
            return False
        if len(x) == 0:
            return False
        try:
            x = np.array(x).astype(float)
            y = np.array(y).astype(float)
        except ValueError:
            return False
        if name == 'cosine' and (np.linalg.norm(x) == 0 or np.linalg.norm(y) == 0):
            return False
        return True

    def euclidean(x, y):
        if not validate_input(x, y):
            return -1
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        if not validate_input(x, y):
            return -1
        return round(np.sum(abs(x - y)), 4)

    def chebyshev(x, y):
        if not validate_input(x, y):
            return -1
        return round(max(abs(x - y)), 4)

    def cosine(x, y):
        if not validate_input(x, y):
            return -1
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        dot_product = np.dot(x, y)
        return round(1 - dot_product / (norm_x * norm_y), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    return metrics.get(name, lambda x, y: -1)

import numpy as np
def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if isinstance(z, list) or isinstance(z, np.ndarray):
        result = [round(x / (1 + abs(x)), 4) for x in z]
        return result
    else:
        return round(z / (1 + abs(z)), 4)

import numpy as np
def build_alias_table(probs: list[float] | np.ndarray) -> tuple[list[float], list[int]]:
    """Builds probability and alias tables for the alias sampling method.

    Args:
        probs: 1-D list or NumPy array of positive floats summing to 1.

    Returns:
        Tuple (prob_table, alias_table) where:
            * prob_table – list of floats rounded to 4 decimals.
            * alias_table – list of ints.
        If *probs* is invalid (negative entries or not summing to 1) return -1.
    """
    if isinstance(probs, list):
        probs = np.array(probs)
    if np.any(probs <= 0):
        return -1
    if not np.isclose(np.sum(probs), 1.0, atol=1e-12):
        return -1
    N = len(probs)
    scaled_probs = probs * N
    small = []
    large = []
    for i in range(N):
        if scaled_probs[i] < 1:
            small.append(i)
        else:
            large.append(i)
    while small and large:
        l = small.pop()
        g = large.pop()
        alias_table[l] = g
        scaled_probs[l] = scaled_probs[l] + scaled_probs[g] - 1
        if scaled_probs[g] >= 1:
            large.append(g)
        else:
            small.append(g)
    for i in large:
        prob_table[i] = 1.0
    for i in small:
        prob_table[i] = 1.0
    prob_table = [round(p, 4) for p in prob_table]
    alias_table = [int(a) for a in alias_table]
    return (prob_table, alias_table)
