def value_network_forward(state: list[float], W1: list[list[float]], W2: list[list[float]], W3: list[list[float]] | list[float]) -> float:
    if len(state) != W1.shape[0]:
        return -1
    h1 = np.tanh(np.dot(state, W1))
    if len(h1) != W2.shape[0]:
        return -1
    h2 = np.tanh(np.dot(h1, W2))
    if isinstance(W3, list):
        if len(h2) != W3[0].shape[0]:
            return -1
    elif len(h2) != W3.shape[0]:
        return -1
    v = np.dot(h2, W3)
    return round(v.item(), 4)
