import numpy as np
def sigmoid(x):
    """element-wise logistic sigmoid."""
    return 1 / (1 + np.exp(-x))
def tanh(x):
    """element-wise hyperbolic tangent."""
    return np.tanh(x)
def relu(x):
    """element-wise Rectified Linear Unit."""
    return np.maximum(0, x)
def leaky_relu(x, alpha=0.01):
    """element-wise Leaky-ReLU with slope *alpha* for x<0."""
    return np.where(x < 0, alpha * x, x)
def softmax(x):
    """numerically stable softmax over a 1-D input."""
    exp_x = np.exp(x - np.max(x))
    return exp_x / exp_x.sum()
def get_activation(name):
    """Return the activation function by *name*.

    Args:
        name (str): One of {"sigmoid", "tanh", "relu", "leaky_relu", "softmax"}

    Returns:
        Callable: the requested activation function.

    Raises:
        ValueError: If *name* is not recognised.
    """
    if name == 'sigmoid':
        return sigmoid
    elif name == 'tanh':
        return tanh
    elif name == 'relu':
        return relu
    elif name == 'leaky_relu':
        return leaky_relu
    elif name == 'softmax':
        return softmax
    else:
        raise ValueError('Invalid activation function.')

import numpy as np
def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    if n_clf < 1:
        n_clf = 1
    weights = np.ones(len(y_train)) / len(y_train)
    predictions = []
    for t in range(n_clf):
        stump = build_stump(X_train, y_train, weights)
        y_pred = stump.predict(X_test)
        epsilon_t = np.sum(weights[y_pred != y_train])
        alpha_t = 0.5 * np.log((1 - epsilon_t) / epsilon_t)
        weights *= np.exp(-alpha_t * y_train * y_pred)
        weights /= np.sum(weights)
        predictions.append(np.sign(np.dot(stump.weights, y_train)))
    return predictions
def build_stump(X_train: np.ndarray, y_train: np.ndarray, weights: np.ndarray) -> 'DecisionStump':

    class DecisionStump:

        def __init__(self, threshold, feature_index):
            self.threshold = threshold
            self.feature_index = feature_index

        def predict(self, x):
            return 1 if x[self.feature_index] >= self.threshold else -1
    best_threshold = None
    best_feature_index = None
    min_error = float('inf')
    for feature_index in range(X_train.shape[1]):
        for threshold in np.unique(X_train[:, feature_index]):
            y_pred = np.where(X_train[:, feature_index] >= threshold, 1, -1)
            error = np.mean(weights[y_pred != y_train])
            if error < min_error:
                min_error = error
                best_threshold = threshold
                best_feature_index = feature_index
    return DecisionStump(best_threshold, best_feature_index)

def king_loss_fn(x: int | float) -> float:
    """Compute King’s piece-wise loss for a scalar value.

    The loss function L(x) is defined by four segments:
      • x ≤ 250:   L(x) = –0.25 * x + 82.50372665317208
      • 250 < x ≤ 600: L(x) = 20.00372665317208
      • 600 < x ≤ 700: L(x) = –0.2 * x + 140.00372665317207
      • x > 700:  L(x) = 0.003726653172066108

    The result must be rounded to 4 decimal places.

    Args:
        x: An integer or float input.

    Returns:
        A float rounded to four decimal places.
    """
    if x <= 250:
        return round(-0.25 * x + 82.50372665317208, 4)
    elif 250 < x <= 600:
        return round(20.00372665317208, 4)
    elif 600 < x <= 700:
        return round(-0.2 * x + 140.00372665317207, 4)
    else:
        return round(0.003726653172066108, 4)

import numpy as np
def L1Regularization(alpha: float):
    """Create a callable object that implements L1 regularisation.

    The returned object must support two operations:
    1. call with a NumPy array to obtain the L1 penalty value
    2. call its .grad(w) method to obtain the gradient of the penalty

    Args:
        alpha: Non-negative float controlling the strength of the regularisation.

    Returns:
        An instance that fulfils the described interface.
    """
    if not isinstance(alpha, float) or alpha < 0:
        raise ValueError('alpha must be a non-negative float')

    def call(w):
        return alpha * np.sum(np.abs(w))

    def grad(w):
        return alpha * np.sign(w)
    return (call, grad)

