class Node:

    def __init__(self, val):
        self.val = val
        self.left = None
        self.right = None

    def __gt__(self, other):
        return self.val > other.val

    def __ge__(self, other):
        return self.val >= other.val

    def __lt__(self, other):
        return self.val < other.val

    def __le__(self, other):
        return self.val <= other.val
def inorder_keys(pairs):
    if not pairs:
        return []

    def build_bst(node, pairs):
        if not pairs:
            return node
        (key, val) = pairs.pop(0)
        if val > node.val:
            node.right = build_bst(Node(val), pairs)
        else:
            node.left = build_bst(Node(val), pairs)
        return node
    root = build_bst(Node(pairs[0][1]), pairs)

    def in_order_traversal(node, result):
        if node is None:
            return
        in_order_traversal(node.left, result)
        result.append(node.val)
        in_order_traversal(node.right, result)
    result = []
    in_order_traversal(root, result)
    return result

import numpy as np
def get_loss(name: str):
    """Return a loss function specified by *name*.

    The function must recognise the following identifiers (case-insensitive,
    underscores are ignored):
        • MAE  →  "mae" | "mean_absolute_error"
        • MSE  →  "mse" | "mean_squared_error"
        • Binary Cross Entropy  →  "cross_entropy" | "log_loss" | "ce"

    The returned object has the signature `fn(y_true, y_pred) -> float` where
    *y_true* and *y_pred* are 1-D sequences (list or NumPy array) of equal
    length.  The resulting float must be rounded to **four** decimal places.

    Args:
        name: Name or alias of the desired loss function.

    Returns:
        A Python callable implementing the requested loss.

    Raises:
        ValueError: If *name* does not correspond to a supported loss.
    """

    def mae(y_true, y_pred):
        return round(np.mean(np.abs(y_true - y_pred)), 4)

    def mse(y_true, y_pred):
        return round(np.mean((y_true - y_pred) ** 2), 4)

    def cross_entropy(y_true, y_pred):
        y_pred = np.clip(y_pred, 1e-15, 1 - 1e-15)
        return round(-np.mean(y_true * np.log(y_pred) + (1 - y_true) * np.log(1 - y_pred)), 4)
    name_map = {'mae': mae, 'mean_absolute_error': mae, 'mse': mse, 'mean_squared_error': mse, 'cross_entropy': cross_entropy, 'log_loss': cross_entropy, 'ce': cross_entropy}
    if name.lower() in name_map:
        return name_map[name.lower()]
    else:
        raise ValueError('Invalid loss function.')

import math
def noam_learning_rate(step: int, model_dim: int=512, scale_factor: float=1.0, warmup_steps: int=4000) -> float:
    """Return the learning-rate given by the Noam schedule.

    The function must follow the formula described in the task description and
    round the result to 9 decimal places.
    """
    if step < 1:
        return 0.0
    lr = scale_factor * model_dim ** (-0.5) * min(math.pow(step, -0.5), step * math.pow(warmup_steps, -1.5))
    return round(lr, 9)

import numpy as np
def get_initializer(name: str):
    """Return a weight-initialisation callable associated with *name*.

    The callable must have the signature
        initializer(shape: tuple[int, ...], scale: float = 1.0) -> np.ndarray
    and create a numpy array filled according to the requested strategy.

    Supported strategies (case-insensitive):
        - "zeros"
        - "ones"
        - "normal"
        - "glorot_uniform"

    If *name* is not recognised the function must raise `ValueError`.
    """
    if name.lower() == 'zeros':
        return lambda shape, scale=1.0: np.zeros(shape)
    elif name.lower() == 'ones':
        return lambda shape, scale=1.0: np.ones(shape)
    elif name.lower() == 'normal':
        return lambda shape, scale=1.0: np.random.normal(scale=scale, size=shape)
    elif name.lower() == 'glorot_uniform':

        def glorot_uniform_init(shape, scale=1.0):
            if len(shape) != 2:
                raise ValueError('Glorot initialization is only supported for two-dimensional arrays.')
            (fan_in, fan_out) = (shape[0], shape[1])
            limit = scale * np.sqrt(6 / (fan_in + fan_out))
            return np.random.uniform(-limit, limit, shape)
        return glorot_uniform_init
    else:
        raise ValueError(f"Unsupported initializer '{name}'.")

import heapq
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    """Generate a Huffman code for the supplied tokens.

    The function must return a dictionary mapping **every** unique token in
    *tokens* to its binary Huffman code.  All codes must be prefix-free and
    consist only of the characters ‘0’ and ‘1’.
    """
    if not tokens:
        return {}
    freq = {}
    for token in tokens:
        if token in freq:
            freq[token] += 1
        else:
            freq[token] = 1
    heap = []
    for (token, count) in freq.items():
        node = {'token': token, 'frequency': count, 'left': None, 'right': None}
        heapq.heappush(heap, node)
    while len(heap) > 1:
        left_node = heapq.heappop(heap)
        right_node = heapq.heappop(heap)
        parent = {'token': '', 'frequency': left_node['frequency'] + right_node['frequency'], 'left': left_node, 'right': right_node}
        heapq.heappush(heap, parent)

    def traverse(node, code=''):
        if node['left']:
            traverse(node['left'], code + '0')
        if node['right']:
            traverse(node['right'], code + '1')
        if node['token']:
            result[node['token']] = code
    result = {}
    traverse(heapq.heappop(heap))
    return result
