import re
from typing import Any, Dict, Optional
class KernelBase:
    """Minimal base class for all kernels.  Do NOT modify this class."""

    def __init__(self, **kwargs: Any) -> None:
        self.params: Dict[str, Any] = kwargs

    def set_params(self, state: Dict[str, Any]) -> None:
        """(Re)sets parameters from a previously stored summary dict."""
        hp = state.get('hyperparameters', {})
        self.params.update({k: v for (k, v) in hp.items() if k != 'id'})
        return self

    def summary(self) -> Dict[str, Any]:
        """Dictionary description that can be fed back to *initialize_kernel*."""
        return {'hyperparameters': {'id': self.__class__.__name__, **self.params}}
class LinearKernel(KernelBase):

    def __init__(self):
        super().__init__()
class PolynomialKernel(KernelBase):

    def __init__(self, degree: int=3, coef0: float=1.0):
        super().__init__(degree=degree, coef0=coef0)
class RBFKernel(KernelBase):

    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, str):
        match = re.match('(Linear|Polynomial|RBF)\\(([^)]+)\\)', param)
        if match:
            name = match.group(1).lower()
            params = match.group(2)
            if name == 'linear':
                return LinearKernel()
            elif name == 'polynomial':
                params_dict = eval(params)
                return PolynomialKernel(**params_dict)
            elif name == 'rbf':
                params_dict = eval(params)
                return RBFKernel(**params_dict)
            else:
                raise NotImplementedError(f'Unknown kernel: {name}')
        else:
            raise ValueError('Invalid string representation')
    if isinstance(param, dict):
        hyperparameters = param.get('hyperparameters', {})
        if 'id' not in hyperparameters:
            raise ValueError("Dictionary must contain 'hyperparameters' entry")
        name = hyperparameters['id'].lower()
        if name == 'linear':
            return LinearKernel()
        elif name == 'polynomial':
            params_dict = hyperparameters.copy()
            del params_dict['id']
            return PolynomialKernel(**params_dict)
        elif name == 'rbf':
            params_dict = hyperparameters.copy()
            del params_dict['id']
            return RBFKernel(**params_dict)
        else:
            raise NotImplementedError(f'Unknown kernel: {name}')
    raise ValueError('Unsupported input format')

import numpy as np
def conv1D(X: np.ndarray, W: np.ndarray, stride: int, pad, dilation: int=0) -> list:
    """Perform a 1-D cross-correlation between *X* and *W*.

    The function must support batches, multiple input/output channels, padding
    (integer, tuple or "same"), arbitrary stride and dilation.  It should return
    the output volume as *list* obtained via ``ndarray.tolist()``.

    Args:
        X: ndarray of shape (n_examples, signal_length, in_channels)
        W: ndarray of shape (kernel_width, in_channels, out_channels)
        stride: positive integer, the step size of the sliding window
        pad:  int, 2-tuple, or the string "same" specifying the amount of zero
              padding to add to the left and right of the signal
        dilation: non-negative integer, number of points inserted between
                   neighbouring kernel elements (0 ⇒ standard convolution)

    Returns:
        A Python list representing the convolved volume with shape
        (n_examples, output_length, out_channels).
    """
    (n_examples, signal_length, in_channels) = X.shape
    (kernel_width, _, out_channels) = W.shape
    if isinstance(pad, str):
        if pad == 'same':
            output_length = (signal_length - 1) // stride + 1
        else:
            raise ValueError('Invalid padding type')
    elif isinstance(pad, int):
        output_length = (signal_length - kernel_width + 2 * pad) // stride + 1
    else:
        raise ValueError('Invalid padding value')
    Y = np.zeros((n_examples, output_length, out_channels), dtype=W.dtype)
    for e in range(n_examples):
        for c in range(in_channels):
            if isinstance(pad, int):
                padded_signal = np.pad(X[e, :, c], ((pad, pad), (0, 0)), mode='constant', constant_values=0)
            elif isinstance(pad, tuple):
                padded_signal = np.pad(X[e, :, c], ((pad[0], pad[1]), (0, 0)), mode='constant', constant_values=0)
            else:
                raise ValueError('Invalid padding value')
            for i in range(output_length):
                for j in range(kernel_width):
                    Y[e, i, c] += padded_signal[i * stride + j, c] * W[j, c, c]
    return Y.tolist()

import numpy as np
def logistic_negative_gradient(y: list, f: list) -> list:
    """
    Calculates the negative gradient for the logistic loss function used in Gradient Boosting.

    Args:
    y (list): Binary class labels (0 or 1).
    f (list): Current prediction scores (floats).

    Returns:
    list: List of negative gradients rounded to four decimal places. Returns -1 if y contains invalid values.
    """
    if not all((isinstance(i, (int, float)) for i in y)):
        return [-1] * len(y)
    g = []
    for (yi, fi) in zip(y, f):
        if yi == 0:
            g.append(-yi / (1 + np.exp(yi * fi)))
        elif yi == 1:
            g.append(yi / (1 + np.exp(yi * fi)))
        else:
            return [-1] * len(y)
    return [round(gi, 4) for gi in g]

import numpy as np
def selu(x, order: int=0):
    """Compute the SELU activation or its derivatives.

    Parameters
    ----------
    x : float | list | np.ndarray
        Input value(s). Can be a scalar, 1-D list/array, or multi-D list/array.
    order : int, default 0
        0 → SELU(x)
        1 → first derivative d(SELU)/dx
        2 → second derivative d²(SELU)/dx²

    Returns
    -------
    list | float
        A Python list (or scalar for scalar input) containing the element-wise
        result, rounded to 4 decimals.
    """
    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    if isinstance(x, (int, float)):
        if order == 0:
            return round(scale * np.maximum(0, x), 4)
        elif order == 1:
            return round(scale * np.where(x > 0, 1, alpha * np.exp(x) - alpha), 4)
        elif order == 2:
            return round(scale * np.where(x > 0, 1, alpha * np.exp(x)), 4)
    elif isinstance(x, np.ndarray):
        if order == 0:
            return np.round(scale * np.maximum(0, x), 4)
        elif order == 1:
            return np.round(scale * np.where(x > 0, 1, alpha * np.exp(x) - alpha), 4)
        elif order == 2:
            return np.round(scale * np.where(x > 0, 1, alpha * np.exp(x)), 4)
    else:
        raise ValueError('Input must be a scalar, 1D list/array, or multi-D list/array')

import numpy as np
def to_one_hot(y: np.ndarray, num_classes: int | None=None) -> np.ndarray:
    """Convert a vector of integer class labels to one-hot encoding.

    Parameters
    ----------
    y : np.ndarray
        1-D or 2-D NumPy array containing integer labels. If the array is
        already one-hot encoded (shape (N, C) with rows summing to 1), the
        function should return a copy of the input.
    num_classes : int, optional
        Total number of classes. If ``None`` the number is inferred from ``y``.

    Returns
    -------
    np.ndarray
        One-hot encoded matrix of shape (N, C) with dtype ``float``.
    """
    if len(y.shape) == 1:
        if np.allclose(np.sum(y, axis=1), 1):
            return y.copy()
        unique_labels = np.unique(y)
        y_encoded = np.zeros((len(y), len(unique_labels)))
        for (i, label) in enumerate(unique_labels):
            y_encoded[y == label, i] = 1.0
        return y_encoded
    elif len(y.shape) == 2 and y.shape[1] == 1:
        if np.allclose(np.sum(y, axis=1), 1):
            return y.copy()
        unique_labels = np.unique(y.flatten())
        y_encoded = np.zeros((len(y), len(unique_labels)))
        for (i, label) in enumerate(unique_labels):
            y_encoded[y.flatten() == label, i] = 1.0
        return y_encoded.reshape(-1, 1)
    else:
        raise ValueError('Input array must be either 1D or 2D with shape (N, 1)')

def get_metric(name: str):
    """Return an evaluation metric function by *name*.

    Supported names:
    - 'accuracy'
    - 'precision'
    - 'recall'
    - 'f1'

    If the name is invalid, the function should return -1.
    The returned metric functions must accept two equal-length iterables
    (y_true, y_pred) containing only 0s and 1s and return the metric value
    rounded to 4 decimal places.
    """
    if name == 'accuracy':

        def accuracy(y_true, y_pred):
            tp = sum((y_true[i] == 1 and y_pred[i] == 1 for i in range(len(y_true))))
            fp = sum((y_true[i] == 0 and y_pred[i] == 1 for i in range(len(y_true))))
            tn = sum((y_true[i] == 0 and y_pred[i] == 0 for i in range(len(y_true))))
            fn = sum((y_true[i] == 1 and y_pred[i] == 0 for i in range(len(y_true))))
            return round((tp + tn) / (tp + tn + fp + fn), 4)
        return accuracy
    elif name == 'precision':

        def precision(y_true, y_pred):
            tp = sum((y_true[i] == 1 and y_pred[i] == 1 for i in range(len(y_true))))
            fp = sum((y_true[i] == 0 and y_pred[i] == 1 for i in range(len(y_true))))
            return round(tp / (tp + fp), 4)
        return precision
    elif name == 'recall':

        def recall(y_true, y_pred):
            tp = sum((y_true[i] == 1 and y_pred[i] == 1 for i in range(len(y_true))))
            fn = sum((y_true[i] == 1 and y_pred[i] == 0 for i in range(len(y_true))))
            return round(tp / (tp + fn), 4)
        return recall
    elif name == 'f1':

        def f1(y_true, y_pred):
            tp = sum((y_true[i] == 1 and y_pred[i] == 1 for i in range(len(y_true))))
            fp = sum((y_true[i] == 0 and y_pred[i] == 1 for i in range(len(y_true))))
            fn = sum((y_true[i] == 1 and y_pred[i] == 0 for i in range(len(y_true))))
            if tp + fp + fn == 0:
                return 0
            p = tp / (tp + fp)
            r = tp / (tp + fn)
            return round(2 * p * r / (p + r), 4)
        return f1
    else:
        return -1

import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:

    def find_neighbors(point, data):
        neighbors = []
        for (i, d) in enumerate(data):
            if np.linalg.norm(d - point) <= eps:
                neighbors.append(i)
        return neighbors

    def expand_cluster(point, neighbors, visited, cluster_id):
        visited.add(point)
        cluster[point] = cluster_id
        for neighbor in neighbors:
            if neighbor not in visited:
                cluster[neighbor] = cluster_id
                new_neighbors = find_neighbors(data[neighbor], data)
                if len(new_neighbors) >= min_samples:
                    expand_cluster(neighbor, new_neighbors, visited, cluster_id)
    n_samples = data.shape[0]
    visited = set()
    cluster = {}
    cluster_id = 0
    for i in range(n_samples):
        if i in visited:
            continue
        neighbors = find_neighbors(data[i], data)
        if len(neighbors) < min_samples:
            cluster[i] = -1
        else:
            cluster_id += 1
            expand_cluster(i, neighbors, visited, cluster_id)
    return list(cluster.values())
