import numpy as np
def softplus(x: 'np.ndarray | list | tuple | float | int', order: int=0):
    """SoftPlus activation function and its derivatives.

    Parameters
    ----------
    x : float | int | list | tuple | np.ndarray
        Input data on which SoftPlus or its derivatives will be computed.
    order : int, optional
        0  -> SoftPlus(x)
        1  -> SoftPlus'(x)
        2  -> SoftPlus''(x)
        Any other value should make the function return -1.

    Returns
    -------
    float | list
        Result rounded to 4 decimal places. The shape mirrors the input. If the
        order is invalid, returns -1.
    """
    if isinstance(x, (list, tuple)):
        x = np.array(x)
    if not isinstance(x, np.ndarray):
        raise ValueError('Input must be a NumPy array.')
    if order < 0 or order > 2:
        return -1
    if order == 0:
        return np.round(np.log(1 + np.exp(x)), 4).tolist()
    elif order == 1:
        return np.round(np.exp(x) / (1 + np.exp(x)), 4).tolist()
    else:
        return np.round(np.exp(x) / (1 + np.exp(x)) ** 2, 4).tolist()

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if isinstance(x, (float, int)):
        if order == 0:
            return round(1 / (1 + np.exp(-x)), 4)
        elif order == 1:
            return round(sigmoid(x, 0) * (1 - sigmoid(x, 0)), 4)
        elif order == 2:
            return round(sigmoid(x, 0) * (1 - sigmoid(x, 0)) * (1 - 2 * sigmoid(x, 0)), 4)
        else:
            return -1
    elif isinstance(x, list):
        return [sigmoid(i, order) for i in x]
    elif isinstance(x, np.ndarray):
        return np.array([sigmoid(i, order) for i in x]).tolist()
    else:
        return -1

import numpy as np
def hamming(window_len: int, symmetric: bool=False) -> list[float]:
    """Generate a Hamming window.

    The function should return *window_len* samples of a Hamming window.
    When *symmetric* is *False*, return the periodic variant (last sample of
    the symmetric window removed).  Each value must be rounded to 4 decimals
    and the result converted to a regular Python list.
    """
    if window_len == 0:
        return []
    elif window_len == 1:
        return [1.0]
    if symmetric:
        return [0.54 - 0.46 * np.cos(2 * np.pi * n / (window_len - 1)) for n in range(window_len)]
    else:
        return [0.54 - 0.46 * np.cos(2 * np.pi * n / (window_len - 1)) for n in range(window_len - 1)]

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if len(X[0]) != len(Y[0]):
        return -1
    if isinstance(sigma, list):
        if len(sigma) != len(X[0]):
            return -1
    if sigma is None:
        sigma = np.sqrt(len(X[0]) / 2)
    K = []
    for i in range(len(X)):
        row = []
        for j in range(len(Y)):
            if sigma == None:
                dist = np.linalg.norm(np.array(X[i]) - np.array(Y[j]))
            else:
                dist = np.linalg.norm((np.array(X[i]) - np.array(Y[j])) / sigma)
            row.append(round(np.exp(-0.5 * dist ** 2), 4))
        K.append(row)
    return K

