from collections import Counter
def knn_classifier(X_train, y_train, X_query, k, method='naive'):
    """k-Nearest Neighbour classifier.

    Parameters
    ----------
    X_train : list[list[float]] | np.ndarray
        Feature matrix of the training set (n_samples × n_features).
    y_train : list[int] | np.ndarray
        Integer labels for training samples.
    X_query : list[list[float]] | np.ndarray
        Feature matrix of the query/test set.
    k : int
        Number of neighbours to consider.
    method : str, default "naive"
        Neighbour search strategy: "naive", "heap" or "kdtree".

    Returns
    -------
    list[int]
        Predicted label for each query sample.
    """

    def euclidean_distance(point1, point2):
        return np.sqrt(np.sum((point1 - point2) ** 2))

    def find_k_nearest_neighbors(X, y, query_point, k):
        if method == 'naive':
            distances = [euclidean_distance(query_point, x) for x in X]
            sorted_indices = np.argsort(distances)[:k]
            return [y[i] for i in sorted_indices]
        elif method == 'heap':
            distances = [euclidean_distance(query_point, x) for x in X]
            min_heap = []
            for (i, distance) in enumerate(distances[:k]):
                heapq.heappush(min_heap, (-distance, i))
            for (distance, index) in zip(distances[k:], range(k, len(X))):
                if distance < -min_heap[0][0]:
                    heapq.heappop(min_heap)
                    heapq.heappush(min_heap, (-distance, index))
            return [y[i] for (_, i) in min_heap]
        elif method == 'kdtree':

            class Node:

                def __init__(self, feature_index=None, value=None, left=None, right=None):
                    self.feature_index = feature_index
                    self.value = value
                    self.left = left
                    self.right = right

            def insert(node, point, depth=0):
                if node is None:
                    return Node(depth % len(point), point)
                if point[node.feature_index] < node.value:
                    node.left = insert(node.left, point, depth + 1)
                else:
                    node.right = insert(node.right, point, depth + 1)
                return node

            def search(node, query_point, depth=0):
                if node is None:
                    return []
                if query_point[node.feature_index] == node.value:
                    neighbors = search(node.left, query_point, depth + 1)
                    neighbors.extend(search(node.right, query_point, depth + 1))
                    return neighbors
                if query_point[node.feature_index] < node.value:
                    return search(node.left, query_point, depth + 1)
                else:
                    return search(node.right, query_point, depth + 1)
            root = None
            for (i, point) in enumerate(X_train):
                root = insert(root, point)
            neighbors = search(root, query_point)
            distances = [euclidean_distance(query_point, neighbor) for neighbor in neighbors]
            sorted_indices = np.argsort(distances)[:k]
            return [y[i] for i in sorted_indices]
    predictions = []
    for query in X_query:
        predictions.append(find_k_nearest_neighbors(X_train, y_train, query, k)[0])
    return predictions
