import numpy as np
def calculate_correlation_matrix(X: np.ndarray, Y: np.ndarray | None=None) -> list | int:

    def compute_covariance(matrix):
        mean = np.mean(matrix, axis=0)
        diff = matrix - mean[:, np.newaxis]
        return np.dot(diff, diff.T) / len(matrix)

    def compute_std_deviation(matrix):
        mean = np.mean(matrix, axis=0)
        diff = matrix - mean[:, np.newaxis]
        return np.sqrt(np.sum(diff ** 2, axis=0) / len(matrix))

    def check_for_zero_std_deviation(matrix):
        std_devs = compute_std_deviation(matrix)
        return np.any(std_devs == 0)
    if Y is None:
        covariance = compute_covariance(X)
        correlation_matrix = [[round(cov, 4) for cov in row] for row in covariance]
    else:
        if check_for_zero_std_deviation(X) or check_for_zero_std_deviation(Y):
            return -1
        covariance = compute_covariance(np.hstack((X, Y)))
        correlation_matrix = [[round(cov, 4) for cov in row] for row in covariance[:X.shape[1]]]
    return correlation_matrix

