import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    header_table = {}

    def construct_fp_tree(transaction, parent=None):
        if not transaction:
            return
        item = transaction[0]
        if item not in header_table:
            header_table[item] = []
        header_table[item].append((transaction, parent))
        construct_fp_tree(transaction[1:], item)
    for transaction in transactions:
        construct_fp_tree(transaction)

    def generate_frequent_item_sets(prefix, node):
        if not node:
            return
        if len(node) == 1:
            item = node[0][0]
            if item in frequent_items:
                frequent_item_set = tuple(sorted(list(prefix + [item])))
                if frequent_item_set not in result:
                    result.append(frequent_item_set)
        else:
            child_nodes = node[0]
            for child_node in child_nodes:
                new_prefix = tuple(sorted(list(prefix + [child_node[0]])))
                generate_frequent_item_sets(new_prefix, child_node[1])
    result = []
    for item in frequent_items:
        generate_frequent_item_sets(tuple([item]), header_table[item])
    result.sort(key=lambda x: (-len(x), x))
    return result
