from typing import Any, Dict
def flatten_parameters(params: Dict[str, Any]) -> Dict[str, Any]:
    """Flatten a nested parameter dictionary.

    A dot (`.`) is used to join nested keys.  The input dictionary must not be
    mutated.

    Args:
        params: A potentially nested dictionary where some values can be other
                dictionaries.

    Returns:
        A new dictionary without nested structures.
    """
    result = {}

    def flatten(key, value):
        if isinstance(value, dict):
            for (k, v) in value.items():
                flatten(f'{key}.{k}', v)
        else:
            result[key] = value
    flatten('', params)
    return result

import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (list, tuple)):
        return [round(1 / (1 + np.exp(-x)), 4) for x in z]
    elif isinstance(z, np.ndarray):
        return np.round(1 / (1 + np.exp(-z)), 4).tolist()
    else:
        return round(1 / (1 + np.exp(-z)), 4)

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    if isinstance(z, list):
        return [sigmoid_grad(item) for item in z]
    elif isinstance(z, np.ndarray):
        return np.array([sigmoid_grad(item) for item in z])
    else:
        return np.round(z * (1 - z), 4)

import numpy as np
def relu_grad(z):
    """Compute the element-wise derivative of the ReLU activation.

    The function should return 0 for every entry that is less than or equal to
    zero and 1 for every entry that is strictly greater than zero.  Scalars
    must yield a single integer; arrays/lists must yield a list of identical
    shape.
    """
    if isinstance(z, (int, float)):
        return int(z > 0)
    elif isinstance(z, (list, np.ndarray)):
        return [int(x > 0) for x in z]
    else:
        raise ValueError('Input must be a scalar, list, or ndarray')

import numpy as np
def sigmoid_(Z):
    """Compute the logistic sigmoid activation element-wise.

    Args:
        Z: A scalar, 1-D list/array, or 2-D list/array of real numbers.

    Returns:
        Sigmoid-transformed value(s) rounded to 4 decimals.  Returns a float for
        scalar input; otherwise returns a Python list preserving the input
        shape.
    """
    if isinstance(Z, (int, float)):
        return round(1 / (1 + np.exp(-Z)), 4)
    elif isinstance(Z, list):
        return [round(1 / (1 + np.exp(-z)), 4) for z in Z]
    elif isinstance(Z, np.ndarray):
        return np.round(1 / (1 + np.exp(-Z)), 4).tolist()
    else:
        raise ValueError('Unsupported input type')

def tree_stats(root: 'node | None') -> tuple[int, int]:
    if root is None:
        return (0, 0)
    leaf_count = 1 if root.results else 0
    leaf_count += tree_stats(root.left)[0] + tree_stats(root.right)[0]
    max_depth = max(tree_stats(root.left)[1], tree_stats(root.right)[1]) + 1
    return (leaf_count, max_depth)

import math
def k_nearest_neighbors(points: list[list[float]], query: list[float], k: int=3) -> list[int]:

    def insert(node, point, depth=0):
        if not node:
            return {'index': len(points), 'split_axis': depth % len(point), 'left': None, 'right': None}
        split_axis = node['split_axis']
        value = point[split_axis]
        if value < node['value']:
            node['left'] = insert(node['left'], point, depth + 1)
        else:
            node['right'] = insert(node['right'], point, depth + 1)
        return node

    def search(node, query, depth=0, results=[]):
        if not node:
            return
        split_axis = node['split_axis']
        value = query[split_axis]
        if value == node['value']:
            dist = sum(((a - b) ** 2 for (a, b) in zip(query, node['point'])))
            if dist <= 1e-06:
                results.append(node['index'])
            search(node['left'], query, depth + 1, results)
            search(node['right'], query, depth + 1, results)
        elif value < node['value']:
            search(node['left'], query, depth + 1, results)
        else:
            search(node['right'], query, depth + 1, results)

    def build_tree(points):
        root = None
        for (i, point) in enumerate(points):
            root = insert(root, point)
        return root

    def get_distance(point1, point2):
        return sum(((a - b) ** 2 for (a, b) in zip(point1, point2)))
    root = build_tree(points)
    results = []
    search(root, query, 0, results)
    results.sort(key=lambda x: (get_distance(points[x], query), x))
    return results[:k]
