k = 2

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if isinstance(features, list):
        features = tuple(features)
    while True:
        if root.y is not None:
            return root.y
        feature_index = root.label
        if feature_index < len(features):
            x_value = features[feature_index]
            if task_type == 'classification':
                if x_value == root.x:
                    return predict_sample(root.child[0], features, task_type)
                else:
                    return predict_sample(root.child[1], features, task_type)
            elif task_type == 'regression':
                if x_value <= root.s:
                    return predict_sample(root.child[0], features, task_type)
                else:
                    return predict_sample(root.child[1], features, task_type)
        else:
            raise ValueError('Feature index out of range')

import numpy as np
import math
def choose_best_feature(X: np.ndarray, y: np.ndarray) -> tuple[int, float]:

    def calculate_entropy(y):
        (_, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        return -np.sum((p * math.log2(p) for p in probabilities))

    def calculate_conditional_entropy(y, feature_indices):
        unique_values = np.unique(X[:, feature_indices])
        total_entropy = calculate_entropy(y)
        conditional_entropies = []
        for value in unique_values:
            subset_y = y[X[:, feature_indices] == value]
            conditional_entropy.append(calculate_entropy(subset_y))
        return sum((entropy * count / len(y) for (entropy, count) in zip(conditional_entropies, np.bincount(X[:, feature_indices]))))
    max_information_gain = -math.inf
    best_feature_index = None
    for feature_idx in range(X.shape[1]):
        info_gain = calculate_conditional_entropy(y, [feature_idx])
        if info_gain > max_information_gain:
            max_information_gain = info_gain
            best_feature_index = feature_idx
    return (best_feature_index, round(max_information_gain, 6))

