from typing import List
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> List[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    if epsilon == 0:
        return []
    np.random.seed(42)

    def is_leaf(node):
        return len(node['samples']) <= 1

    def calculate_path_length(sample_index, node):
        if is_leaf(node):
            return 0
        elif sample_index <= node['split']['threshold']:
            return 1 + calculate_path_length(sample_index, node['left'])
        else:
            return 1 + calculate_path_length(sample_index, node['right'])

    def grow_tree(node):
        if len(node['samples']) > 1:
            feature_index = np.random.randint(len(node['features']))
            min_value = np.min(node['values'][feature_index])
            max_value = np.max(node['values'][feature_index])
            threshold = np.random.uniform(min_value, max_value)
            left_split = []
            right_split = []
            for sample in node['samples']:
                if sample[feature_index] <= threshold:
                    left_split.append(sample)
                else:
                    right_split.append(sample)
            node['left'] = {'samples': left_split, 'features': node['features'], 'values': node['values']}
            node['right'] = {'samples': right_split, 'features': node['features'], 'values': node['values']}
            left_path_length = calculate_path_length(0, node['left'])
            right_path_length = calculate_path_length(0, node['right'])
            if left_path_length <= right_path_length:
                node['split'] = {'type': 'left', 'threshold': threshold}
            else:
                node['split'] = {'type': 'right', 'threshold': threshold}

    def build_isolation_forest():
        root = {'samples': data, 'features': list(range(data.shape[1])), 'values': [list(np.min(data, axis=0)), list(np.max(data, axis=0))], 'left': None, 'right': None, 'split': None}
        for _ in range(n_trees):
            grow_tree(root)
        return root

    def calculate_average_path_length(tree):
        total_path_length = 0
        count = 0

        def traverse(node):
            nonlocal total_path_length, count
            if is_leaf(node):
                total_path_length += calculate_path_length(0, node)
                count += 1
            else:
                traverse(node['left'])
                traverse(node['right'])
        traverse(root)
        return total_path_length / count if count > 0 else 0

    def find_anomalies(root, epsilon):
        anomalies = []

        def traverse(node):
            nonlocal anomalies
            if is_leaf(node):
                path_length = calculate_path_length(0, node)
                avg_path_length = calculate_average_path_length(root)
                score = 2 ** (-path_length / avg_path_length)
                if score >= epsilon:
                    anomalies.append(node['index'])
            elif node['split']['type'] == 'left':
                traverse(node['left'])
            else:
                traverse(node['right'])
        traverse(root)
        return sorted(anomalies)
    root = build_isolation_forest()
    anomalies = find_anomalies(root, epsilon)
    return anomalies

