import numpy as np
def softplus(x, deriv: bool=False):
    """
    Compute the SoftPlus activation function or its gradient.

    Parameters:
    - x: A scalar, Python list, or NumPy array of real numbers.
    - deriv: A boolean flag indicating whether to compute the derivative (gradient) of the SoftPlus function.

    Returns:
    - A Python list (or a single float if the input is a scalar) containing the computed SoftPlus values or gradients,
      rounded to the nearest 4th decimal.
    """
    x_array = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x_array))
    else:
        result = np.log1p(np.exp(-np.abs(x_array))) + np.maximum(x_array, 0)
    rounded_result = np.round(result, decimals=4)
    if isinstance(x, (list, tuple)):
        return rounded_result.tolist()
    elif isinstance(x, np.ndarray):
        return rounded_result.tolist()
    else:
        return float(rounded_result)

