class FPTree:

    def __init__(self):
        self.root = FPNode(None, None)
        self.header_table = {}

    def insert(self, transaction, count):
        current_node = self.root
        for item in transaction:
            if item not in current_node.children:
                new_node = FPNode(item, current_node)
                current_node.children[item] = new_node
                if item not in self.header_table:
                    self.header_table[item] = []
                self.header_table[item].append(new_node)
            else:
                new_node = current_node.children[item]
            new_node.count += count
            current_node = new_node

    def get_conditional_pattern_base(self, item):
        if item not in self.header_table:
            return []
        pattern_base = []
        for node in self.header_table[item]:
            path = []
            while node.parent != self.root:
                path.append((node.item, node.count))
                node = node.parent
            if path:
                pattern_base.append(path[::-1])
