    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    fp_tree = FPTree()
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        filtered_transaction.sort(key=lambda x: (-frequent_items[x], x))
        fp_tree.insert(filtered_transaction, 1)

    def mine_fp_tree(tree, prefix, frequent_itemsets):
        if not tree.header_table:
            return
        sorted_items = sorted(tree.header_table.keys(), key=lambda x: (-frequent_items[x], x))
        for item in sorted_items:
            new_prefix = prefix + [item]
