            conditional_pattern_base = tree.get_conditional_pattern_base(item)
            conditional_tree = FPTree()
            conditional_item_counts = Counter()
            for pattern in conditional_pattern_base:
                conditional_item_counts.update(pattern)
                conditional_tree.insert(pattern, 1)
            mine_fp_tree(conditional_tree, new_prefix, frequent_itemsets)
