def split_data(X, y, feature, threshold):
    """Split the dataset into left and right based on the feature and threshold."""
    left_indices = X[:, feature] <= threshold
    right_indices = X[:, feature] > threshold
    return (X[left_indices], y[left_indices], X[right_indices], y[right_indices])
def build_tree(X, y, depth=0, max_depth=None):
    """Recursively build the decision tree."""
    if len(y) == 0:
        return DecisionTreeNode(value=None)
    if len(set(y)) == 1:
        return DecisionTreeNode(value=y[0])
    if max_depth is not None and depth >= max_depth:
        return DecisionTreeNode(value=Counter(y).most_common(1)[0][0])
    (best_feature, best_threshold, best_gain) = (None, None, -np.inf)
    n_features = X.shape[1]
    for feature in range(n_features):
        unique_values = np.unique(X[:, feature])
        for i in range(1, len(unique_values)):
            threshold = (unique_values[i - 1] + unique_values[i]) / 2
            (X_left, y_left, X_right, y_right) = split_data(X, y, feature, threshold)
            gain = gini_gain(y, y_left, y_right)
            if gain > best_gain:
                best_gain = gain
                best_feature = feature
                best_threshold = threshold
    if best_gain == -np.inf:
        return DecisionTreeNode(value=Counter(y).most_common(1)[0][0])
    (X_left, y_left, X_right, y_right) = split_data(X, y, best_feature, best_threshold)
    left_node = build_tree(X_left, y_left, depth + 1, max_depth)
    right_node = build_tree(X_right, y_right, depth + 1, max_depth)
    return DecisionTreeNode(feature=best_feature, threshold=best_threshold, left=left_node, right=right_node)
