from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        if isinstance(tree_a.value, (int, float, np.ndarray, list)):
            return np.allclose(tree_a.value, tree_b.value)
        else:
            return tree_a.value == tree_b.value
    if isinstance(tree_a, Leaf) != isinstance(tree_b, Leaf):
        return False
    if tree_a.feature != tree_b.feature or not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
        return False
    return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)

import numpy as np
def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    n = X.shape[0]
    W = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            if i != j:
                W[i, j] = 1 / (1 + np.linalg.norm(X[i] - X[j]))
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    E = eigenvectors[:, :k]

    def kmeans(E, k, max_iter=100):
        centroids = E[:k].copy()
        labels = np.zeros(n, dtype=int)
        for _ in range(max_iter):
            for i in range(n):
                distances = np.linalg.norm(E[i] - centroids, axis=1)
                labels[i] = np.argmin(distances)
            new_centroids = np.array([E[labels == j].mean(axis=0) for j in range(k)])
            if np.all(centroids == new_centroids):
                break
            centroids = new_centroids
        for j in range(k):
            if np.sum(labels == j) == 0:
                centroids[j] = E[np.random.randint(n)]
        return labels.tolist()
    return kmeans(E, k)

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(mel):
        return 700 * (10 ** (mel / 2595) - 1)
    min_mel = hz_to_mel(min_freq)
    max_mel = hz_to_mel(max_freq)
    mels = np.linspace(min_mel, max_mel, n_filters + 2)
    freqs = mel_to_hz(mels)
    filterbank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        left_mel = mels[i]
        center_mel = mels[i + 1]
        right_mel = mels[i + 2]
        left_freq = mel_to_hz(left_mel)
        center_freq = mel_to_hz(center_mel)
        right_freq = mel_to_hz(right_mel)
        for k in range(N // 2 + 1):
            freq = k * fs / N
            if freq < left_freq:
                weight = 0
            elif left_freq <= freq < center_freq:
                weight = (freq - left_freq) / (center_freq - left_freq)
            elif center_freq <= freq < right_freq:
                weight = (right_freq - freq) / (right_freq - center_freq)
            else:
                weight = 0
            filterbank[i, k] = weight
    if normalize:
        for i in range(n_filters):
            filterbank[i] *= 2 / (freqs[i + 2] - freqs[i])
    filterbank_list = [[round(weight, 4) for weight in row] for row in filterbank]
    return filterbank_list

